n		var CONTIPLAY = 1;\r\n        /*处理自动播放选项和cookie*/\r\n        (function() {\r\n            var Tool = CommonTool;\r\n            var chk = Tool.byId(\'J_Video_Autoplay\');\r\n            var ua = navigator.userAgent.toLowerCase();\r\n            var isIOS = /\\((iPhone|iPad|iPod)/i.test(ua);\r\n            if (isIOS) {\r\n                console.log(chk.parentNode.style.display);\r\n				//chk.parentNode.style.display = \'none\'; //2015.11.25 隐藏自动播放选项\r\n                return;\r\n            }\r\n			//chk.parentNode.style.display = \'\'; //2015.11.25 隐藏自动播放选项\r\n            var clickCookie = function() {\r\n                Tool.bindEvent(chk, \'change\',\r\n                function() {\r\n                    var chked = chk.checked;\r\n                    Tool.writeCookie(\'ArtiVAuto\', (chked ? 1 : 0), 24 * 365 * 10, \'/\', \'.sina.com.cn\');\r\n                });\r\n            }\r\n            var byCookie = function() {\r\n                var coo = Tool.readCookie(\'ArtiVAuto\');\r\n                if (coo) {\r\n                    if (parseInt(coo) == 0) {\r\n                        chk.checked = false;\r\n                        AUTOPLAY = 0;\r\n                    }\r\n                }\r\n            };\r\n            clickCookie();\r\n            byCookie();\r\n        })();\r\n        /*获取第一个视频vid*/\r\n        var firstItem = CommonTool.byClass(\'J_Play_Item\', \'J_Play_List\')[0];\r\n        var fInfo = firstItem.getAttribute(\'play-data\').split(\'-\');\r\n        var fVid = fInfo[0];\r\n        var fPid = fInfo[1];\r\n        var sinaBokePlayerConfig_o = {\r\n            container: \"J_Article_Player\", //Div容器的id\r\n            width: 525,\r\n            height: 430,\r\n            playerWidth: 525, //宽\r\n            playerHeight: 430, //高\r\n            autoLoad: 1, //自动加载\r\n            autoPlay: AUTOPLAY, //自动播放\r\n            as: 0, //广告\r\n            pid: fPid,\r\n            tjAD: 0, //显示擎天柱广告\r\n            tj: 0, //片尾推荐\r\n            continuePlayer: 1, //连续播放\r\n            casualPlay: 1, //任意拖动视频\r\n            head: 0, //播放片头动画\r\n            logo: 0, //显示logo\r\n            share: 0,\r\n			thumbUrl: \"\"\r\n        };\r\n    </script> \n <script src=\"http://sjs2.sinajs.cn/video/sinaplayer/js/page/player.js\"></script> \n <script type=\"text/javascript\">\r\n        (function() {\r\n            var toggle = function(id, hide) {\r\n                var e = CommonTool.byId(id);\r\n                var par = e.parentNode;\r\n                if (hide) {\r\n                    CommonTool.addClass(par, e.className + \'_disabled\');\r\n                } else {\r\n                    CommonTool.removeClass(par, e.className + \'_disabled\');\r\n                }\r\n            }\r\n            var scroll = new ScrollPic();\r\n            scroll.scrollContId = \"J_Play_List\"; //内容容器ID\r\n            scroll.arrLeftId = \"J_Player_Prev\"; //左箭头ID\r\n            scroll.arrRightId = \"J_Player_Next\"; //右箭头ID\r\n            scroll.listEvent = \"onclick\"; //切换事件\r\n            scroll.frameWidth = 532; //显示框宽度 **显示框宽度必须是翻页宽度的倍数\r\n            scroll.pageWidth = 133 * 3; //翻页宽度\r\n            scroll.upright = false; //垂直滚动\r\n            scroll.speed = 10; //移动速度(单位毫秒，越小越快)\r\n            scroll.space = 15; //每次移动像素(单位px，越大越快)\r\n            scroll.autoPlay = false; //自动播放\r\n            scroll.autoPlayTime = 5; //自动播放间隔时间(秒)\r\n            scroll.circularly = false;\r\n            scroll._move = scroll.move;\r\n            scroll.move = function(num, quick) {\r\n                scroll._move(num, quick);\r\n                toggle(scroll.arrRightId, scroll.eof);\r\n                toggle(scroll.arrLeftId, scroll.bof);\r\n            };\r\n            scroll.initialize(); //初始化\r\n            toggle(scroll.arrLeftId, scroll.bof);\r\n        })();\r\n    </script> \n <script>\r\n	var autoPlayCheck = $(\"#J_Video_Autoplay\")[0].checked;\r\n		var autoPlayFlv = 0;\r\n		var autoPlayH5 = false;\r\n		if(autoPlayCheck){\r\n			autoPlayFlv = 1;\r\n			var autoPlayH5 = true;\r\n		}\r\n	var flashConfig = {\r\n		url: \"\",	// flash播放器地址,\r\n		container : \"J_Article_Player\",\r\n		id: \"myMovie\",\r\n		width: 525,\r\n		height: 430,\r\n			params : {\r\n			 allowNetworking : \"all\",\r\n			 allowScriptAccess : \"always\",\r\n			 wmode : \"opaque\",\r\n			 allowFullScreen : \"true\",\r\n			 quality : \"high\"\r\n			},\r\n			attributes: {},\r\n			flashvars: {\r\n				//autoPlay: autoPlayFlv,  //是否自动播放\r\n				autoPlay: 0,  //11.25通知改成0\r\n				loop: 0,		\r\n				autoLoad: 1,\r\n				thumbUrl: \'\',\r\n				tj: 0,\r\n				as: 0\r\n			},\r\n			h5attr: {\r\n				//autoPlay: autoPlayH5,  //是否自动播放\r\n				autoPlay: false,  //11.25通知改成false\r\n				controls: true, //是否显示控制条\r\n				loop: false,		\r\n				poster: \'\', //视频加载前欲加载的图片地址，即播放器一开始显示的截图\r\n				preload: \'auto\'\r\n			}\r\n	  };\r\n		var videoList = [\r\n						{\r\n				video_id: 250925630,\r\n				pid:1,\r\n				url: \'http://video.sina.com.cn/view/250925630.html\',\r\n				title:\'鸿雁与伴侣对望亲吻感动主人\'//,\r\n			//swfOutsideUrl:\"http://you.video.sina.com.cn/api/sinawebApi/outplayrefer.php/video_id=250925630/s.swf\"\r\n			},\r\n		]\r\n	</script> \n <script>\r\n	(function($){\r\n	var Play = {\r\n		init: function(flashConfig, videoList){\r\n			this.flashConfig = flashConfig;\r\n			this.videoList = videoList;\r\n			this.playVideo = playVideo;\r\n			this.prev = this.current = 0;\r\n			this.length = this.videoList.length;\r\n			this.contNode = $(\"#J_Video_Autoplay\");\r\n			this.titleNode = $(\"#J_Video_Source\");\r\n			this.playListNode = $(\"#J_Play_List .J_Play_Item\");\r\n			if(videoList.length!==1){\r\n				//显示小图\r\n				CommonTool.byId(\'J_Play_List_Wrap\').style.display=\'\';\r\n			}\r\n			if(videoList.length<5){\r\n				CommonTool.byId(\'J_Player_Prev\').style.display=\'none\';\r\n				CommonTool.byId(\'J_Player_Next\').style.display=\'none\';\r\n			}\r\n			this.initPlayer();\r\n			this.bind();\r\n		},\r\n		bind: function(){\r\n			var _this = this;\r\n			$(\"#J_Play_List\").on(\"click\", \".J_Play_Item a\", function(e){\r\n				e.preventDefault();\r\n				_this.playCurrent($(this));\r\n			});\r\n		},\r\n		initPlayer: function(){\r\n			var _this = this;\r\n			this.player = this.playVideo(this.flashConfig);\r\n			this.player.init(this.videoList[this.prev]);\r\n			this.player.on(\"playCompleted\", function(){\r\n				_this.playNext();\r\n			});\r\n			this.playListNode.eq(0).addClass(\"selected\");\r\n			this.titleNode.html(this.videoList[0].title);\r\n		},\r\n		playCurrent: function($this){\r\n			this.prev = this.current;\r\n			this.current = $this.parents(\".J_Play_Item\").index();\r\n			this.play(this.prev, this.current);\r\n		},\r\n		playNext: function(){\r\n			if(!this.contNode[0].checked){\r\n				return;\r\n			}\r\n			this.prev = this.current;\r\n			if(this.current >= this.length - 1){\r\n				return;\r\n			}\r\n			this.current++;\r\n			this.play(this.prev, this.current);\r\n		},\r\n		play: function(prev, current){\r\n			this.player.playVideo(this.videoList[current]);\r\n			this.titleNode.html(this.videoList[current].title);\r\n			this.playListNode.eq(prev).removeClass(\"selected\");\r\n			this.playListNode.eq(current).addClass(\"selected\");\r\n		}\r\n	}\r\n	Play.init(flashConfig, videoList);\r\n})(jQuery);\r\n</script> \n</div> \n<!--mce-plugin-videoList2[{\"listPlay\":true,\"videos\":[{\"title\":\"\\u9e3f\\u96c1\\u4e0e\\u4f34\\u4fa3\\u5bf9\\u671b\\u4eb2\\u543b\\u611f\\u52a8\\u4e3b\\u4eba\",\"videoid\":\"250925630\",\"ad\":true,\"source\":\"\",\"url\":\"http://video.sina.com.cn/view/250925630.html\",\"img\":\"http://p.ivideo.sina.com.cn/video/250/925/630/250925630_400_300.jpg\",\"largeImg\":\"http://p.ivideo.sina.com.cn/video/250/925/630/250925630_400_300.jpg\",\"smallImg\":\"http://p.ivideo.sina.com.cn/video/250/925/630/250925630_400_300.jpg\",\"tags\":\"\"}],\"downloadDescr\":\"\",\"videoCard\":false}]mce-plugin-videoList2--> \n<p>　　原标题：“两只大鹅”的故事重演这次是大家想要的结局</p> \n<p>　　现代快报/ZAKER南京记者 王晓宇</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/transform/20170106/-bOX-fxzkfuk2381003.jpg\" alt=\"\" data-link=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/transform/20170106/QjqI-fxzkssy0845032.jpg\" alt=\"\" data-link=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　去年2月，广东一对大鹅生离死别的“爱情故事”感动了无数网友。近日，类似的故事在连云港重演，一段离别视频在网上热传。据拍摄者祁先生介绍，视频中被网友称为大鹅的其实是他养殖的鸿雁，事发当天，他原本想把一对鸿雁出售给外地的一个客户，但是现场的一幕让他非常感动，于是当场决定不卖了。</p> \n<p>　　<strong>视频 “两只大鹅的故事”重演</strong></p> \n<p>　　近日，有网友爆出一段视频，曾经火爆朋友圈的两只大鹅的故事又重新上演了，视频中，两只大鹅面临分别时的不舍和失落，让人看了顿时动容。</p> \n<p>　　画面一开始，一只绑着双腿的大鹅被放在一辆货车上，它伸长脖子，不停地张嘴叫着，并试图去靠近站在地上的另一只大鹅，而站在地上的大鹅显得十分失落，站在原地一动不动，两眼深情地望着对方。画面结束的瞬间，站在地上的大鹅突然一跃，可能是想靠近对方身边。</p> \n<p>　<strong>　真相 同样的剧本，主角换成了鸿雁</strong></p> \n<p>　　1月5日，现代快报记者联系到了视频的拍摄者祁先生，据祁先生介绍，视频拍摄于元旦节前后，视频中的禽类其实并不是真正的家鹅，而是他养殖的鸿雁，是家鹅的祖先。祁先生告诉记者，他是连云港市灌南县田楼镇人，他有一个养殖场，饲养鸿雁，平时他会向全国各地的客户出售自己饲养的鸿雁，有供顾客食用的，有给人家饲养的，供人饲养的都是一对一对地出售。</p> \n<p>　　视频拍摄当天，祁先生应一位外地客户要求，准备将一对鸿雁绑好后发往外地，就在他将其中一只公的绑好刚放上车后，令他意想不到的一幕出现了，“那只母的可能以为只带走对方，就站在车边上，非常不舍。”祁先生说，“我当时就联想到去年网上传的两只大鹅分别时候的画面，没想到在我这里重演了，我当时就拿手机将这个场景拍了下来。”</p> \n<p>　　拍下视频后，祁先生又将视频传到网上，没想到，短短几天，点击率就超过百万。</p> \n<p>　　<strong>结局 主人被感动，当场决定不卖了</strong></p> \n<p>　　祁先生告诉记者，由于被两只鸿雁的不舍表现感动，他当时就决定不卖了，然后将视频中的两只鸿雁留下来自己饲养。</p> \n<p>　　被留下来的两只鸿雁显得非常开心。重获自由的它们还找祁先生算账，在祁先生给记者提供的另外一段视频中，被放了的两只鸿雁追着祁先生试图攻击，其中一只用嘴巴咬住祁先生的衣服，逼得祁先生不停地奔跑，场面非常有趣，“动物就跟我们人一样，可能在怪我为什么要卖掉它们吧！”祁先生说。</p> \n<p class=\"article-editor\">责任编辑：李鹏 </p>', '2017-04-05 16:46:08', '原标题：“两只大鹅”的故事重演这次是大家想要的结局现代快报/ZAKER南京记者王晓宇去年2月，广东一对大鹅生离死别的“爱情故事”感动了无数网友。近日，类似的故事在连云港重演，一段离别视频在网上热传。据拍摄者祁先生介绍，视频中被网友称为大鹅的', '0', null, null, '1', '0', '鸿雁,大鹅,视频,对望', '2017-04-05 16:46:08', '1', null, null, '0', null, 'noarchive', '鸿雁离别与伴侣对望亲吻 主人被感动决定不卖', null, '1');
INSERT INTO `tz_content` VALUES ('92', 'keke', null, null, '<p>　　原标题：男子在警方官微侮辱因公牺牲民警被行政处罚</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/crawl/20170404/PnDR-fycwyph1949418.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p class=\"text\">　　四川遂宁小伙唐某在浏览遂宁市公安局发布的祭奠因公牺牲民警新浪微博时，发表不当言论公然侮辱英烈。澎湃新闻（www.thepaper.cn）4月4日从遂宁市公安局了解到，嫌疑人唐某对自己的违法行为深感后悔，通过新浪微博发表道歉申明，目前警方已对其做出行政处罚。</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/crawl/20170404/kcvo-fycxmks5597693.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p class=\"text\">　　4月3日晚，遂宁市公安局官方微博@遂宁公安 通报称，4月2日下午，25岁遂宁大英县人唐某在位于重庆市万州区一出租屋内浏览新浪微博时，在遂宁市公安局官方微博@遂宁公安 发布的清明节前夕祭奠全市因公牺牲民警博文中，发表不当言论公然侮辱英烈，造成恶劣的社会影响。</p> \n<p class=\"text\">　　通报称，在调查过程中，唐某对自己的违法行为深感后悔，如实交待了自己的违法行为，且通过个人微博发表道歉申明积极消除社会影响，根据《中华人民共和国治安管理处罚法》相关规定，目前遂宁警方对违法行为人唐某依法作出了行政处罚。</p> \n<p>　　来源：澎湃新闻</p> \n<p class=\"article-editor\">责任编辑：李鹏 </p>', '2017-04-05 16:46:09', '原标题：男子在警方官微侮辱因公牺牲民警被行政处罚来源：澎湃新闻', '0', null, null, '1', '0', '民警,侮辱,牺牲', '2017-04-05 16:46:09', '1', null, null, '0', null, 'noarchive', '男子在警方官微侮辱因公牺牲民警被行政处罚', null, '1');
INSERT INTO `tz_content` VALUES ('93', 'keke', null, null, '<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/transform/20170405/V9B_-fycxmks5712633.jpg\" alt=\"\" data-link=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　本报讯（华商晨报记者&nbsp;汤洋）90后情侣宋晓飞和周晓彤分手后，宋晓飞多次向周晓彤求复合。</p> \n<p>　　在二人见面的时候，宋晓飞将周晓彤掐死，然后自己喝下农药，还用剪刀扎了自己，称他们是相约自杀。</p> \n<p>　　近日，法院发布案件判决结果，宋晓飞被认定故意杀人犯罪，因其取得周晓彤家人谅解，一审获轻判，被判有期徒刑十五年。</p> \n<p><strong>　　90后情侣闹分手</strong></p> \n<p><strong>　　小伙求复合求见面</strong></p> \n<p>　　宋晓飞和周晓彤都是90后，宋晓飞25岁，周晓彤21岁，两人是一对小情侣。</p> \n<p>　　虽然已经相处两年多了，但周晓彤家里对这段感情并不认可，他们认为宋晓飞有孩子，和周晓彤并不合适。但后来看周晓彤自己愿意，也就不管了。</p> \n<p>　　周晓彤的母亲回忆，2016年5月17日晚上，宋晓飞和周晓彤打电话的时候，她听见两个人说分手了，宋晓飞还在电话里吓唬周晓彤了，“周晓彤把电话给我了，我跟他争辩，他就开始骂我，挺难听的。”</p> \n<p>　　周晓彤的母亲说，她曾听周晓彤说过，宋晓飞脾气不好，还小心眼，还曾吓唬周晓彤说要是分手就整死我们全家。</p> \n<p>　　周晓彤的工友证实，两人偶尔吵架，但关系还行。后来分手，是因为宋晓飞把周晓彤的妈妈给骂了。</p> \n<p>　　之后，宋晓飞多次给周晓彤发短信，挽留周晓彤，并请求跟周晓彤见面。</p> \n<p>　<strong>　亲手掐死女友</strong></p> \n<p><strong>　　喝药又扎自己没死成</strong></p> \n<p>　　2016年5月22日11时许，宋晓飞从葫芦岛乘火车来到沈阳，跟周晓彤见面。之后，两人打车到沈阳市浑南区的一处树林里，一直停留到18时。</p> \n<p>　　按照宋晓飞的说法，他以前和周晓彤的妈妈吵过架，想让周晓彤原谅他，但周晓彤说不会原谅他了。他虽然嘴上说不原谅就不原谅吧，但还是拿出事先准备好的耗子药，“威胁”周晓彤，“你要是不原谅我，我就喝药，她没让我喝。”</p> \n<p>　　宋晓飞说，之后和周晓彤又唠了一会别的，“聊着聊着周晓彤说她不想活了，让我把药给她喝。我问她真想一起死吗？她说是。我就和周晓彤说我掐死你，然后我喝耗子药。她说行，就躺下了。我就用双手掐她的脖子，一直到她不动了我才松手。”</p> \n<p>　　周晓彤死后，宋晓飞把准备好的老鼠药，一个是瓶的，一个是袋的，全喝了。他怕不死，又从附近一个卖鱼的人那买了一把剪刀，回到周晓彤身边，用剪刀扎了自己左胸口两刀。</p> \n<p>　　之后，宋晓飞给他的姐夫打了电话，说他和周晓彤自杀了，让姐夫照顾好他爸妈和女儿。</p> \n<p>　　打完电话，宋晓飞躺在周晓彤身上，感觉越来越难受。</p> \n<p>　　按照宋晓飞的说法，后来，他哥哥给他打电话问他在哪，还让他报警，他也打了110，但没有打通……</p> \n<p>　　最终，宋晓飞被送到医院后抢救过来，但21岁的周晓彤因口鼻及颈部遭受钝性外力导致机械性窒息而死亡。</p> \n<p>　<strong>　律师称是相约自杀</strong></p> \n<p><strong>　　法院认定故意杀人</strong></p> \n<p>　　2016年12月，检察机关将此案公诉到法院。</p> \n<p>　　法院开庭审理此案时，宋晓飞的辩护律师提出，此案是因恋爱情感引发的案件，属于民间矛盾激化引发的犯罪；而且，宋晓飞的行为符合相约自杀的情形；宋晓飞有自首情节，请求对其从轻处罚。</p> \n<p>　　在案件审理过程中，周晓彤的家人与宋晓飞及宋晓飞的亲属就赔偿经济损失事宜达成调解协议。</p> \n<p>　　法院审理此案认为，宋晓飞扼掐周晓彤颈部，致周晓彤当场死亡，其行为构成故意杀人罪。</p> \n<p>　　经查，本案是由恋爱情感引发，属于民间矛盾激化引发的犯罪。但没有证据证明周晓彤有自杀倾向，也没有证据证明周晓彤有自杀的行为，两人不属于相约自杀。周晓彤的死亡是由宋晓飞用手扼掐其颈部直接造成，宋晓飞对周晓彤的死亡后果应负完全的法律责任。</p> \n<p>　　宋晓飞非法故意剥夺他人生命，后果严重，应予惩处。鉴于本案系因情感纠纷引发，宋晓飞有自首情节，且积极赔偿周晓彤亲属经济损失，取得谅解，对宋晓飞可从轻处罚。</p> \n<p>　　近日，法院发布案件判决结果，一审认定宋晓飞犯故意杀人罪，判处有期徒刑十五年。（文中人物均为化名）</p> \n<p class=\"article-editor\">责任编辑：张迪 </p>', '2017-04-05 16:46:09', '本报讯（华商晨报记者&nbsp;汤洋）90后情侣宋晓飞和周晓彤分手后，宋晓飞多次向周晓彤求复合。在二人见面的时候，宋晓飞将周晓彤掐死，然后自己喝下农药，还用剪刀扎了自己，称他们是相约自杀。近日，法院发布案件判决结果，宋晓飞被认定故意杀人犯罪', '0', null, null, '1', '0', '分手,情侣,拍案', '2017-04-05 16:46:09', '1', null, null, '0', null, 'noarchive', '小伙不愿分手掐死女友后喝药 谎称二人相约自杀', null, '1');
INSERT INTO `tz_content` VALUES ('94', 'keke', null, null, '<div class=\"img_wrapper\"> \n <img alt=\"当地时间2017年4月3日，俄罗斯莫斯科，电脑屏幕上显示圣彼得堡地铁爆炸案疑似嫌疑人的监控画面。图片来源：视觉中国\" src=\"http://n.sinaimg.cn/news/transform/20170404/1q4m-fycxmks5559030.jpg\" /> \n <span class=\"img_descr\">　　当地时间2017年4月3日，俄罗斯莫斯科，电脑屏幕上显示圣彼得堡地铁爆炸案疑似嫌疑人的监控画面。图片来源：视觉中国</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"当地时间4月3日，俄罗斯圣彼得堡，圣彼得堡地铁“干草广场站”和“技术学院站”之间发生爆炸。\" src=\"http://n.sinaimg.cn/news/transform/20170404/Y-pb-fycwyph1910235.jpg\" /> \n <span class=\"img_descr\">当地时间4月3日，俄罗斯圣彼得堡，圣彼得堡地铁“干草广场站”和“技术学院站”之间发生爆炸。</span> \n</div> \n<p>　　中新社圣彼得堡4月4日电 (记者 王修君)据俄塔社4日消息，在圣彼得堡地铁实施爆炸的犯罪嫌疑人身份已初步确定。此人与叙利亚极端分子有联系。</p> \n<p>　　消息援引有关部门消息人士的话说，根据初步分析得知，该名犯罪嫌疑人出生于中亚地区，与叙利亚极端分子有联系。</p> \n<p>　　消息称，该名犯罪嫌疑人当时或贴身携带、或通过背包或手提包把炸弹带入地铁车厢，然后在地铁行进过程中引爆了炸弹。</p> \n<p>　　消息说，根据分析死者及死者周边人被炸部位可以得知，炸弹是在位于胃部的高度被引爆的。目前犯罪嫌疑人残肢已被找到，并被送往相关机构进一步检验。</p> \n<p>　　4月3日，圣彼得堡地铁“技术学院”站和“先纳亚广场”站之间发生爆炸，造成11人死亡、45人受伤。俄罗斯总检察院已将这一事件定性为恐怖袭击。</p> \n<p>　　俄罗斯总统普京于3日向遇难者家属致以了“最深切的同情和慰问”。同日普京还前往“技术学院”站向地铁爆炸遇难者献花，表示哀悼。(完)</p> \n<table align=\"left\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\"> \n <tbody> \n  <tr> \n   <td>&nbsp;</td> \n  </tr> \n </tbody> \n</table> \n<p class=\"article-editor\">责任编辑：陈琰 SN225</p>', '2017-04-05 16:46:09', '中新社圣彼得堡4月4日电(记者王修君)据俄塔社4日消息，在圣彼得堡地铁实施爆炸的犯罪嫌疑人身份已初步确定。此人与叙利亚极端分子有联系。消息援引有关部门消息人士的话说，根据初步分析得知，该名犯罪嫌疑人出生于中亚地区，与叙利亚极端分子有联系。消', '0', null, null, '1', '0', '爆炸,地铁,圣彼得堡', '2017-04-05 16:46:09', '1', null, null, '0', null, 'noarchive', '俄地铁爆炸嫌犯身份初步确认 炸弹在胃部被引爆', null, '1');
INSERT INTO `tz_content` VALUES ('95', 'keke', null, null, '<p><strong>　　原标题：共享单车不要成为公共交通的定时炸弹</strong></p> \n<p><strong>　　作者：郑伟彬</strong></p> \n<p>　　为了拼市场第一、为了成为共享单车领域的“滴滴”，共享单车又开始了网约车的竞争模式——借助于资本的力量，用免费、补贴等竞争手段来清扫战场、打败对手。遇到特别是像清明这样的小假期，城市内可游玩的景点有限，必然导致共享单车的高度集中，成为引爆公共交通混乱的定时炸弹。</p> \n<p>　　近来，共享单车负面新闻频发。清明小假期，万余辆共享单车挤爆深圳湾公园为其再增一例。因为人行道、自行车道变成共享单车的停车场，公众在滨海观光道上“寸步难行”，最后深圳市公园管理中心不得不发出紧急公告，4月4日全天禁止共享单车进入深圳湾公园。</p> \n<p>　　这种现象并非只是深圳湾公园面临的问题，国内很多景点也面临同样的问题。在杭州西湖，由于自行车道车多人多，有些人甚至理直气壮地将共享单车骑到机动车道上，交通秩序被扰乱不说，安全也成为一大问题。</p> \n<p>　　本来，共享单车可解决公众出行“最后一公里”的难题，值得大力推广——有关数据显示，上海市民每次乘坐轨道交通平均耗时62分钟，其中在地铁内的时间仅有35分钟，其余的27分钟则用于两头接驳。</p> \n<p>　　而相较于传统的城市公共自行车“画地为牢”的模式，共享单车自由灵活，因此更能满足公众的真正需要。</p> \n<p>　　但倘若按照现在的方式继续下去，恐怕不仅不能解决问题，反而成为城市的治理难题，成为城市交通的肿瘤。解决共享单车随意停放问题，是共享单车必须翻越的大山。</p> \n<p>　　有人认为，由于共享单车与用户是租赁关系，因此当用户使用单车时，意味着对于公共交通的责任转移到了消费者身上。这种说法看似正确，却恐怕难以真正落实。</p> \n<p>　　比如，共享单车企业是否能够提供相关证明，表明责任在用户身上，而不是其他因素所为？像没有GPS定位功能的ofo单车，就无法做到。</p> \n<p>　　即使如摩拜单车、hellobike等有定位功能，能否准确判断，单车乱停乱放是消费者结束骑行时所为，还是后面有人移动所致，也会是一个问题。</p> \n<p>　　即便能够判定，共享单车企业又如何来约束用户呢？像摩拜单车的APP上，虽然有设置信用分，但在目前阶段，共享单车企业为了吸引用户骑行，增加用户与使用频率、时长等，只会一味讨好消费者，又岂会真的使用这一手段来惩罚用户，注销用户的使用资格，把用户推向竞争对手？</p> \n<p>　　而最最关键的问题，恐怕还是在于共享单车的竞争模式上。为了拼市场第一、为了成为共享单车领域的“滴滴”，共享单车又开始了网约车的竞争模式——借助于资本的力量，用免费、补贴等竞争手段来清扫战场、打败对手；为了提高覆盖率，不计成本、盲目投放。</p> \n<p>　　可以说，缺乏约束手段，盲目投放，免费大战，导致上述局面的出现。特别是像清明这样的小假期，城市内可游玩的景点有限，必然导致共享单车的高度集中，成为引爆公共交通混乱的定时炸弹。</p> \n<p>　　如果共享单车企业不能解决上述问题，最终只会走向地方政府监管市场运营的结局。此前，上海市质监局还组织上海自行车行业协会、国内共享单车主要产地自行车行业协会共同拟订了一份《共享自行车服务规范》征求意见稿。虽然并非强制实施的政策，却已是一种预警。</p> \n<p>　　在能用技术、市场手段解决问题的时候，最好还是借用技术与市场之手，不要等到政府强制介入为好。</p> \n<p>　　更进一步来说，共享单车的问题，也暴露了当前中国互联网创新创业存在的弊病。有些看起来很红火的领域，其实只是资本作怪，而非真正创新引发的热潮。因此，现在谈中国的互联网正从C2C（copy to China）转变为KFC（Kaobei from China）恐怕也是早些了。中国互联网要真正领先全球，还需要更多实实在在的创新。</p> \n<p>　　假期+免费，或许正将成为让共享单车形象扫地的最后一根稻草。</p> \n<p class=\"article-editor\">责任编辑：魏巍 </p>', '2017-04-05 16:46:09', '共享单车,公共交通,定时炸弹', '0', null, null, '1', '0', 'urlpath:pl/; allCIDs:51927,257,51895,56266,258,38790', '2017-04-05 16:46:09', '1', null, null, '0', null, 'noarchive', '共享单车不要成为公共交通的定时炸弹', null, '1');
INSERT INTO `tz_content` VALUES ('96', 'keke', null, null, '<p>　　原标题：朝鲜称世界将见证对朝制裁被粉碎</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/transform/20170404/Py3a-fycwymx3700094.jpg\" alt=\"\" data-mcesrc=\"https://timgsa.baidu.com/timg?image&amp;quality=80&amp;size=b9999_10000&amp;sec=1491304535594&amp;di=16af22994a40b9694b416da0637861a1&amp;imgtype=0&amp;src=http%3A%2F%2Fimg3.cache.netease.com%2Fcnews%2F2014%2F1%2F15%2F20140115015920dbf3d.jpg\" data-mceselected=\"1\" data-link=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　央视网消息：据朝中社3日报道，朝鲜外务省发言人称，世界很快将会见证对朝制裁被粉碎。朝鲜外务省发言人当天在回答朝中社记者提问时说，3月29日，美国众议院外交委员会在一天之内通过了“强化对朝交易制裁法案”“指定朝鲜为支持恐怖主义国家法案”，以及“谴责朝鲜洲际弹道火箭决议案”。3月31日，美国财政部也发布了对朝追加制裁名单。美国强化对朝制裁把本已因联合军演紧张的朝鲜半岛局势推向爆发边缘。发言人说，美国不要妄想通过制裁去除朝鲜核遏制力。世界很快将见证，朝鲜通过怎样的大事件来粉碎针对朝鲜的凶恶盲目制裁。</p> \n<p class=\"article-editor\">责任编辑：李鹏 </p>', '2017-04-05 16:46:09', '原标题：朝鲜称世界将见证对朝制裁被粉碎央视网消息：据朝中社3日报道，朝鲜外务省发言人称，世界很快将会见证对朝制裁被粉碎。朝鲜外务省发言人当天在回答朝中社记者提问时说，3月29日，美国众议院外交委员会在一天之内通过了“强化对朝交易制裁法案”“', '0', null, null, '1', '0', '朝鲜,见证,发言人', '2017-04-05 16:46:09', '1', null, null, '0', null, 'noarchive', '朝鲜：世界很快将会见证对朝制裁被粉碎', null, '1');
INSERT INTO `tz_content` VALUES ('97', 'keke', null, null, '<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/transform/20170405/yegj-fycwymx3836097.jpg\" alt=\"乐天会长辛东彬接受CNN的专访 资料图\" data-mcesrc=\"http://n.sinaimg.cn/mil/crawl/20170405/mvbB-fycxmks5643256.jpg\" data-mceselected=\"1\" data-link=\"\" />\n <span class=\"img_descr\">乐天会长辛东彬接受CNN的专访 资料图<br /></span>\n</div> \n<p>　　[环球网报道 记者 丁洁芸]据韩国《朝鲜日报》4日报道，乐天集团因向韩国国防部提供“萨德”用地而遭到中方持续的“报复”，韩国市民团体“为创造新韩国的国民运动”（以下称国民运动）开展“拯救乐天”市民运动。</p> \n<p>　　报道称，主导该运动的国民运动共同执行委员长徐京硕（音译）从上月末开始开展“拯救乐天”的运动，其中包括奖励购买乐天产品的运动等。</p> \n<p>　　据悉，成立于2014年5月的国民运动是一个具有保守倾向的市民团体，在全国范围内拥有约3万名的会员。</p> \n<p>　　徐京硕表示：“乐天在明知会遭到中国政府‘报复’的情况下，依然决定向国防部提供‘萨德’用地，是基于国家安全考虑的顾全大局的决定”，“身为韩国国民，不能对乐天的痛苦坐视不理”。国民运动自3月24日起，在首尔、釜山、大邱、仁川等韩国16个城市的乐天卖场前，向消费者宣传围绕“萨德”部署的安保状况，并举行了奖励购买乐天产品的活动。</p> \n<p>　　不过，尽管乐天集团的业务在中国市场遭遇了“重大冲击”，其仍然不打算撤出中国。乐天集团3日表示，所谓缩减在华投资是谣言，乐天集团将继续投资在华业务。乐天集团除了等待，没有更好的应对策略。乐天集团一位不愿透漏具体信息的官员表示，“作为市场，中国还没有完全发展，特别是在中西部地区。为了实现全球化发展，中国市场是不可或缺的。”</p> \n<p>　　另据韩国《韩民族日报》5日报道称，“萨德”火势已经从流通业蔓延至制造业，韩国整个产业界都在战战兢兢。韩国现代起亚汽车3月在华销量缩水一半。报道援引现代汽车方面消息表示，3月在华市场销量（5.6026万辆）同比减少44.3%。起亚汽车的销量（1.6006万辆）则同比减少68.0%。两家公司汽车销量共计7.2032万辆，减幅达52.2%。</p> \n<p>　　此前韩国曾向世界贸易组织提出抗议，称中国对韩国部署“萨德”进行贸易反制，对相关韩国企业进行限制。对此，中国外交部发言人曾表示，没听说过所谓的限制措施。中国政府重视中韩经贸合作，欢迎包括韩国企业在内的外国企业来华投资兴业，也会依法保护外国企业在华合法利益，但前提是有关企业在华经营必须依法合规。</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:09', '[环球网报道记者丁洁芸]据韩国《朝鲜日报》4日报道，乐天集团因向韩国国防部提供“萨德”用地而遭到中方持续的“报复”，韩国市民团体“为创造新韩国的国民运动”（以下称国民运动）开展“拯救乐天”市民运动。报道称，主导该运动的国民运动共同执行委员长', '0', null, null, '1', '0', '中韩,乐天,萨德', '2017-04-05 16:46:09', '1', null, null, '0', null, 'noarchive', '韩国开展“拯救乐天”运动 乐天坚持不撤出中国', null, '1');
INSERT INTO `tz_content` VALUES ('98', 'keke', null, null, '<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/translate/20170405/1kvs-fycxmks5703293.jpg\" alt=\"广州市公安局官方微博截图\" data-mcesrc=\"http://n.sinaimg.cn/translate/20170405/1kvs-fycxmks5703293.jpg\" data-mcestyle=\"max-width: 500px;\" data-mceselected=\"1\" data-link=\"\" />\n <span class=\"img_descr\">广州市公安局官方微博截图</span>\n</div> \n<p>　　中新网4月5日电 据广州市公安局官方微博消息，3日，有2名男子徒手爬上猎德大桥桥顶，警方接报后迅速赶到现场进行相关工作。经查，两男子爬上猎德大桥桥顶是为了自拍寻求刺激，目前，海珠警方已依法对刘某、许某作出行政拘留的处罚。</p> \n<p>　　4月3日18时30分许，110报警服务台接到热心群众报警，称有2名男子徒手爬上猎德大桥桥顶。接报后，广州警方立即指令交警、消防、水警、海珠区公安分局及“120”医疗急救中心等各部门人员迅速到位协助开展交通疏导、现场防护、江面巡查、人员救援等处置工作。</p> \n<p>　　经调取视频监控录像，民警发现当天18时12分，两名年轻男子沿猎德大桥西南方向悬索爬至猎德大桥顶部，其中1名男子身背黑色背囊。接报警后，警方立即通报桥梁管理部门协助上桥顶搜寻，排除安全隐患，同时安排无人机围绕猎德大桥搜寻爬桥者。至4日零时，2名爬桥男子自行返回桥面，警方立即将其控制并带回调查。其间，猎德大桥及周边道路因受事件影响，出现交通拥堵。</p> \n<p>　　经查，爬桥男子刘某（19岁，重庆人，无业）、许某（18岁，广东省潮州市人，为广东省某职业技术学校学生，均持有残疾人证，有听力语言障碍）供认，其两人爬上猎德大桥桥顶是为了自拍寻求刺激，并通过网络媒体发布增加关注度。</p> \n<p>　　目前，海珠警方已依法对刘某、许某作出行政拘留的处罚。警方严正提醒：对采取爬桥等方式扰乱公共秩序、损害公众利益的违法行为，无论出于任何目的，警方都将依法查处，请勿以身试法。</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:10', '中新网4月5日电据广州市公安局官方微博消息，3日，有2名男子徒手爬上猎德大桥桥顶，警方接报后迅速赶到现场进行相关工作。经查，两男子爬上猎德大桥桥顶是为了自拍寻求刺激，目前，海珠警方已依法对刘某、许某作出行政拘留的处罚。4月3日18时30分许', '0', null, null, '1', '0', '警方,广州,拘留', '2017-04-05 16:46:10', '1', null, null, '0', null, 'noarchive', '2名男子为寻刺激徒手爬上大桥被拘', null, '1');
INSERT INTO `tz_content` VALUES ('99', 'keke', null, null, '<p>　　中新网4月5日电 据美国地质勘探局消息，伊朗西北部北京时间14点09分发生里氏6.1级地震，震源深度10公里。</p> \n<p>&nbsp;</p> \n<p>&nbsp;</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:10', '中新网4月5日电据美国地质勘探局消息，伊朗西北部北京时间14点09分发生里氏6.1级地震，震源深度10公里。&nbsp;&nbsp;', '0', null, null, '1', '0', '地震,伊朗,震源深度', '2017-04-05 16:46:10', '1', null, null, '0', null, 'noarchive', '伊朗西北部发生里氏6.1级地震 震源深度10公里', null, '1');
INSERT INTO `tz_content` VALUES ('100', 'keke', null, null, '<p>　　[雄安新区概念股迎A股首秀，开盘逾40个股集体涨停！]今日，雄安概念股开盘集体上演涨停潮，冀东水泥、金隅股份、华夏幸福、京汉股份、巨力索具、唐山港、北京城建等逾40只个股纷纷一字封涨停，河北板块个股几乎全线涨停。美股和港股方面，相关概念股亦表现亮眼，连续大涨。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/gXvl-fycwyph2068290.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　4月3日，受雄安新区设立的消息影响，港股金隅股份近日连续大涨。继前一交易日大涨34%后，港股金隅股份今日早盘再度大涨超8%。</p> \n<p>　　今天也是A股清明节后开市首日，雄安概念股开盘集体上演涨停潮，冀东水泥、金隅股份、华夏幸福、京汉股份、巨力索具、唐山港、北京城建等逾40只个股纷纷一字封涨停，河北板块个股几乎全线涨停。</p> \n<p>　　美股市场，雄安新区概念股亦表现活跃，领涨中概股行情。其中，中国汽车物流收涨54.89%至2.85美元/股，英利绿色能源收涨13.33%至2.89美元/股。</p> \n<p>　　<strong>人民日报：别让炒房矮化了雄安新区的千年大计</strong></p> \n<p>　　4月3日，人民日报评论公众号发表署名文章：别让炒房矮化了雄安新区的千年大计。文章指出：想靠炒房搭乘雄安新区这趟“春天专列”，恐怕是会错了意。 &nbsp;</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/aJPZ-fycwymx3869915.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　雄安新区建设是一个重大历史机遇，但肯定不会成为又一个资本拉动的造城运动，不是一些人一厢情愿认定的“声东击西的迁都”，也不是在通州北京副中心之外，再建一个“首都副中心”。长远看来，顶层设计先行，政策措施跟上，就不会偏离目标，任由各路资金投机炒作、哄抬房价地价，干扰新区的建设与发展。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/Dm0z-fycxmks5713325.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　对此，广发策略发表研报称，被称为“千年大计、国家大事”，这一高度几乎是前所未有的，而接下来必将还有密集的配套政策出台，这就有望改善沉寂多时的“风险偏好”，当盈利和利率趋势陷入“钝化”之后，为短期市场趋势带来向上突破的力量。</p> \n<p>　　<strong>如何投资？看看专家的攻略。。。</strong></p> \n<p>　　今天的盘面，雄安概念股无疑是最大的热点。很多个股都是出现了涨停，也就是说很多投资人想买，可是买不到。一方面是非常的有热情，一方面是深深的遗憾，接下来带来的可能是想追高的一个冲动。那么应该怎么理性的来对待这个机会，一般的普通投资者这时候该怎么办？</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/az8t-fycwyns4888719.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　华商基金基金经理 李双全：从雄安新区的定位来看，手笔非常大，定位非常高，足见党中央国务院对这个新区的定位其实是抱有非常高的一个期望。所以从这个层次上来讲，雄安新区的概念不是昙花一现，而是具备一个中长期的价值投资的逻辑在里面，中间周期可能会拉的相对比较长。所以在这个过程当中，今天一字板很多投资者没有抢到，我相信在这个过程当中，尤其是后续的走势也会出现一些分化。我们作为投资者，尤其是专业的机构的投资者，应该仔细去甄别区域内的上市公司。</p> \n<p>　　看看哪些公司是真正的在这个区域有比较好的资源储备或者项目储备，或者说能够从区域的规划建设到发展当中能够享受到区域振兴的红利，而且能够体现到上市公司的营收和利润上。这样的话经过阶段性的一个主题性质的上涨之后，我们从分化当中能够得到更好的、更实在的一个投资机会，也就是中长期的价值投资机会。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/MmU--fycwymx3869917.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　中航证券首席经济学家 许维鸿：我们对于投资者来讲，雄安新区是一个热点。我们投资讲，不买热点，不买追高。如果你现在各路资本都看到了机会，那就不是投资的机会。这时候你就要去想，这时候如果经济再平衡了，以雄安新区为支点，从中央来讲，希望我们的资本是不是能东南沿海逐渐能够流一部分回来来支持我西北、东北的发展，河北省内也是一样。那我现在的这些资本，是不是可以逐渐从北京和天津移一部分出去，来照顾整个河北的一些资产价格的洼地，这个是我们应该投资的。你不要跟别人投，跟别人投一定是亏的。因为热点，大家都看明白逻辑就不是逻辑了。</p> \n<p>　　所以雄安新区，恰恰是中国经济再平衡的一个过程，那资本应该顺着这个脉络，去寻找那些别人还没有看到的这个价值洼地。短期内的热炒是必须的，但是你如果冷静下来，中国经济再平衡以后，逐渐会提供更多的一些价值洼地给投资者来投的。</p> \n<p>　　<strong>建设雄安新区！疏解非首都功能</strong></p> \n<p>　　国家发改委主任何立峰日前接受新华社专访，对中共中央设立河北雄安新区的台前幕后进行介绍。他表示，规划建设雄安新区，是疏解北京非首都功能一个重要的组成部分。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/FdkX-fycxmks5713327.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　据统计，目前北京市人口已经达到2100多万，接近2020年人口数量达到2300万调控目标。人口数量过多带来交通拥堵、房价高涨、资源超负荷等大城市病。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/UhSR-fycwyns4888722.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　规划建设雄安新区，一是重点打造北京非首都功能疏解集中承载地，有效缓解北京大城市病，与北京城市副中心形成北京新的两翼。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/SjrX-fycwyph2068294.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　二是有利于加快补齐区域发展短板，提升河北经济社会发展水平，培育新的区域增长极。也可与冬奥会举办地张北地区共同形成河北新的两翼。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/1khD-fycwyph2068297.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　三是有利于优化京津冀城市布局和空间结构，打造全国创新驱动发展新引擎，加快构建京津冀世界级城市群。&nbsp;</p> \n<p>　　<strong>雄安新区：依法管控土地 建设和房地产交易</strong></p> \n<p>　　记者昨晚从雄安新区筹委会获悉，针对雄安新区设立后一些媒体关于炒房人员到新区聚集、房价上涨等报道情况，安新、容城、雄县三县已经依法对土地、建设、房地产交易等进行管控。</p> \n<p>　　雄安新区发布的消息称，任何以收取定金、意向金等变相销售“五证”不全商品房都属于违法行为，购买此类房屋不受法律保护。雄安新区将严格执行房地产调控政策，严厉打击二手房和小产权房违规交易，严厉打击违规建设，严厉打击黑中介非法销售、夸大宣传、哄抬价格等行为。</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:10', '[雄安新区概念股迎A股首秀，开盘逾40个股集体涨停！]今日，雄安概念股开盘集体上演涨停潮，冀东水泥、金隅股份、华夏幸福、京汉股份、巨力索具、唐山港、北京城建等逾40只个股纷纷一字封涨停，河北板块个股几乎全线涨停。美股和港股方面，相关概念股亦', '0', null, null, '1', '0', '炒作,雄安新区,涨停', '2017-04-05 16:46:10', '1', null, null, '0', null, 'noarchive', '雄安新区概念迎A股首秀 几乎全线涨停', null, '1');
INSERT INTO `tz_content` VALUES ('101', 'keke', null, null, '<p>　　原标题：外媒：地铁爆炸时普京正在圣彼得堡视察</p> \n<div class=\"img_wrapper\">\n <img title=\"1491224967613787.jpg\" src=\"http://n.sinaimg.cn/news/crawl/20170403/ZFhY-fycwyns4603414.jpg\" alt=\"海外网4月3日电 据英国独立报消息，圣彼得堡地铁爆炸案发生时，俄总统弗拉基米尔&middot;普京正视察该市。\" width=\"550\" height=\"Infinity\" border=\"0\" vspace=\"0\" />\n</div> \n<p>　　海外网4月3日电 据英国独立报消息，圣彼得堡地铁爆炸案发生时，俄总统弗拉基米尔&middot;普京正视察该市。</p> \n<p>　　爆炸事件发生时普京正打算与白俄罗斯总统亚历山大&middot;卢卡申科会面。普京在爆炸时是否已经在该市，目前尚不清楚。</p> \n<p>　　俄总统新闻秘书佩斯科夫表示，地铁爆炸案已经向普京汇报，普京正在该市郊外的斯特列利纳。普京表示，我们在考虑所有可能性，包括恐袭。他表示他已责成国安部门立案调查。</p> \n<p>　　此前多家媒体消息称，爆炸造成至少10人死亡，50人受伤，7处地铁站关闭，爆炸200~300当量。炸弹内含有钢钉。受伤者包括儿童。</p> \n<p>　　来源：海外网</p> \n<p class=\"article-editor\">责任编辑：李鹏 </p>', '2017-04-05 16:46:10', '原标题：外媒：地铁爆炸时普京正在圣彼得堡视察海外网4月3日电据英国独立报消息，圣彼得堡地铁爆炸案发生时，俄总统弗拉基米尔·普京正视察该市。爆炸事件发生时普京正打算与白俄罗斯总统亚历山大·卢卡申科会面。普京在爆炸时是否已经在该市，目前尚不清楚', '0', null, null, '1', '0', '爆炸,普京,圣彼得堡', '2017-04-05 16:46:10', '1', null, null, '0', null, 'noarchive', '外媒：俄圣彼得堡地铁爆炸时 普京正在当地视察', null, '1');
INSERT INTO `tz_content` VALUES ('102', 'keke', null, null, '<p>　　原标题：黎巴嫩遭“垃圾围城” 垃圾袋堆满街道空气呛人</p> \n<p>　　中新网2月24日电 据外媒报道，日前，黎巴嫩政府取消了将垃圾出口到俄罗斯的计划，首都贝鲁特持续了半年多的垃圾危机又回到原点，城市里垃圾堆积成山，空气呛人。<img src=\"http://n.sinaimg.cn/translate/20160224/X5zu-fxpruhz7301672.jpg\" alt=\"\" />当地时间2月23日，黎巴嫩垃圾危机持续，垃圾袋堆满贝鲁特街道。</p> \n<p>　　黎巴嫩重建与发展理事会(CDR)称，负责执行将垃圾出口到俄罗斯进行处理的英国公司，未能在本月19日的最后期限前获得文件，证明俄罗斯方面同意接收这些垃圾，因而这项处理垃圾的协议被废止。</p> \n<p>　　这意味着，黎巴嫩政府将获得该公司250万美金的保证金。然而这同时意味着，黎巴嫩已经持续超过半年的垃圾危机仍然难寻解决之道，贝鲁特仍将遭“垃圾围城”。<img src=\"http://n.sinaimg.cn/translate/20160224/y0ou-fxpruhz7301674.jpg\" alt=\"\" />当地时间2月23日，黎巴嫩垃圾危机持续，垃圾袋堆满贝鲁特街道。</p> \n<p>　　1997年，黎巴嫩政府在贝鲁特南部开设了一个垃圾临时填埋场，一直使用到2015年。当地居民称生活受到了极大影响，并开始封锁前往垃圾填埋场的道路。</p> \n<p>　　迫于民众压力，黎巴嫩政府于2015年7月17日关闭了该垃圾填埋场，负责清理垃圾的公司因合约期满也停止工作，这导致贝鲁特街头的垃圾无人处理，堆积如山。一些民众焚烧垃圾产生的烟气，又让环境加剧恶化。<img src=\"http://n.sinaimg.cn/translate/20160224/l8Yg-fxpruhz7301676.jpg\" alt=\"\" />当地时间2月23日，黎巴嫩垃圾危机持续，垃圾袋堆满贝鲁特街道。</p> \n<p>　　抗议者称，“垃圾围城”与黎巴嫩瘫痪的政治制度息息相关。自2014年5月25日前任总统苏莱曼结束任期以来，黎巴嫩总统职位一直空缺。目前，由政府暂时行使宪法赋予的管理国家事务职能，但由于各政治派别分歧严重，政府无法在一些重大问题上作出决策。</p> \n<p>　　在此前的全国对话会议上，黎巴嫩总理塔马姆&middot;萨拉姆称，如果出口计划告吹，他将要求重新考虑原来的垃圾填埋场计划，并且还表示，已经要求对焚烧计划加以研究。</p> \n<p class=\"article-editor\">责任编辑：康亮 </p>', '2017-04-05 16:46:10', '原标题：黎巴嫩遭“垃圾围城”垃圾袋堆满街道空气呛人中新网2月24日电据外媒报道，日前，黎巴嫩政府取消了将垃圾出口到俄罗斯的计划，首都贝鲁特持续了半年多的垃圾危机又回到原点，城市里垃圾堆积成山，空气呛人。当地时间2月23日，黎巴嫩垃圾危机持续', '0', null, null, '1', '0', '黎巴嫩遭“垃圾围城” 垃圾袋堆满街道空气呛人', '2017-04-05 16:46:10', '1', null, null, '0', null, 'noarchive', '黎巴嫩遭“垃圾围城” 垃圾袋堆满街道空气呛人', null, '1');
INSERT INTO `tz_content` VALUES ('103', 'keke', null, null, '<p>　　原标题：韩统一部：韩女足在朝平安无事 当地氛围无异常</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/crawl/20170405/KJst-fycxmks5725166.jpg\" alt=\"韩女足\" />\n <span class=\"img_descr\">韩女足</span>\n</div> \n<p>　　[环球网报道&nbsp;记者&nbsp;李小飞]据韩联社4月5日报道，韩国统一部5日表示，在平壤参加2018年女足亚洲杯预选赛的韩国女足目前平安无事，平壤当地的氛围也无异常。</p> \n<p>　　韩国统一部官员当天表示，平壤目前并无异状，韩国将始终密切关注朝方动静。在韩国江原道江陵参加世界女子冰球锦标赛的朝鲜女子冰球队也无特殊情况。就两地赛事是否照常进行的提问，该官员回答说，韩国政府坚持比赛照国际惯例、规定和程序进行的立场没有改变。</p> \n<p>　　韩国统一部发言人李德行在5日举行的例行记者会上就朝鲜试射导弹一事表示，朝鲜的行为对其想要解决的问题绝无帮助，韩方敦促朝鲜停止“挑衅”，同时警告朝鲜，“挑衅”只会给国际社会和平与稳定构成威胁，让自身为难。</p> \n<p>　　被问及朝鲜射弹目的时，李德行表示，朝鲜在多重目的下频繁“挑衅”，要掌握其真正意图并不容易。</p> \n<p class=\"article-editor\">责任编辑：李鹏 </p>', '2017-04-05 16:46:10', '原标题：韩统一部：韩女足在朝平安无事当地氛围无异常[环球网报道&nbsp;记者&nbsp;李小飞]据韩联社4月5日报道，韩国统一部5日表示，在平壤参加2018年女足亚洲杯预选赛的韩国女足目前平安无事，平壤当地的氛围也无异常。韩国统一部官员当', '0', null, null, '1', '0', '女足,朝鲜,平安', '2017-04-05 16:46:10', '1', null, null, '0', null, 'noarchive', '韩回应女足朝鲜参赛:平安无事 平壤氛围无异常', null, '1');
INSERT INTO `tz_content` VALUES ('104', 'keke', null, null, '<p class=\"otitle\">　　原标题：安徽一邮递员专拆纪委信件 给被举报人通风报信换钱</p> \n<p>　　（法制晚报记者 洪雪 吴洁 编辑 吴洁）安徽一投递员鲍某，打起用自己天天经手的信件换钱的主意。为此，他专门私拆寄给县政府、县委和纪检等部门的举报信件，不仅如此，鲍某还将3封举报县教育局副局长和7封举报县法院副院长的信件提供给被举报人以换取钱财。</p> \n<p>　　深读记者今日获悉，安徽省阜阳市中级于2017年2月7日作出终判决，以私自开拆、隐匿邮件罪，判处有期徒刑一年十个月。</p> \n<div class=\"img_wrapper\">\n <img id=\"netease1491298773953605\" src=\"http://n.sinaimg.cn/news/crawl/20170404/59Zd-fycwymx3719097.png\" alt=\"案情\" />\n <span class=\"img_descr\"><strong>案情</strong></span>\n</div> \n<p>　　<strong>邮递员私拆举报信</strong></p> \n<p>　　<strong>卖给被举报者</strong></p> \n<p>　　深读记者了解到，44岁的鲍某是安徽省颖上县人，初中文化，案发前在安徽省阜阳市颍上县邮政局做投递员。因涉嫌犯私自开拆、隐匿邮件罪，于2016年6月2日被颍上县公安局刑事拘留，2016年7月4日被逮捕。</p> \n<p>　　安徽省颍上县法院审理认定，2007年至2016年4月，鲍某利用担任安徽省阜阳市颍上县邮政局投递员的职务之便，私自开拆邮寄至颍上县委、颍上县人民政府、颍上县纪律检查委员会等部门的信件30余封，隐匿40余封。</p> \n<p>　　此后，鲍某将私自开拆的举报颍上县教育局原副局长李某的3封信件提供给李某；将私自开拆的举报颍上县人民法院原副院长王某某的7封信件提供给王某某，并收取王某某给予的白酒及现金。其余信件被鲍某隐匿于其驾驶的工作车辆内的座位下方和黑色公文包中。</p> \n<p>　　<strong>一审</strong></p> \n<p>　　<strong>举报信私交被举报人</strong></p> \n<p>　　<strong>应从重处罚</strong></p> \n<p>　　颍上县法院审理认为，鲍某身为颍上县邮政局的邮政工作人员，利用职务之便，非法私自开拆邮件30余封、隐匿邮件40余封，其行为已构成私自开拆、隐匿邮件罪，应依法惩处。</p> \n<p>　　鲍某到案后能够如实供述犯罪事实，当庭自愿认罪，构成坦白，依法可从轻处罚。但鲍某为谋取个人私利，私自开拆、隐匿人民群众邮寄给党委、政府、纪检等部门的信件并将部分举报信件私自交给被举报人，严重损害了党和政府在人民群众心中的光辉形象，破坏了国家廉政制度建设，依法亦应从重处罚。</p> \n<p>　　该案经安徽省颍上县法院审判委员会讨论决定后作出一审判决，以鲍某犯私自开拆、隐匿邮件罪，判处有期徒刑一年十个月。</p> \n<p>　　<strong>终审</strong></p> \n<p>　　<strong>破坏国家廉政建设</strong></p> \n<p>　　<strong>应从重处罚</strong></p> \n<p>　　一审判决后，鲍某不服，提起上诉。</p> \n<p>　　鲍某表示，自己能如实供述自己的罪行，并未造成严重的后果，原判对其量刑过重，请求法院从轻、减轻处罚。鲍某的辩护人亦提出了同样的辩护意见。</p> \n<p>　　针对鲍某的上诉理由及其辩护人的辩护意见，二审法院经查，鲍某利用工作之便私拆、隐匿邮件，侵犯了公民的通信自由权，破坏了国家邮电事业的正常运行秩序。鲍某将举报信件私自交给被举报人，严重损害了党和政府在人民群众心中的光辉形象，破坏了国家廉政制度建设，依法亦应从重处罚。</p> \n<p>　　鲍某私自开拆、隐匿邮件犯罪事实清楚，原判根据其犯罪事实、性质、情节及对社会的危害程度，依法在法定刑幅度内量刑，且已就其到案后如实供述自己罪行的情节予以从轻处罚，二审期间鲍某及其辩护人亦未提交影响本案犯罪事实认定及量刑的新的证据，因此鲍某请求再次从轻、减轻处罚于法无据。对于鲍某的上诉理由及其辩护人的辩护意见不能成立，法院不予支持。</p> \n<p>　　法院认为，鲍某身为国家邮政工作人员利用职务之便私拆、隐匿邮件，其行为已构成私自开拆、隐匿邮件罪。原判认定事实清楚，证据确实、充分，定罪准确，量刑适当，审判程序合法。</p> \n<p>　　2月7日，安徽省阜阳市中级于作出终审裁定，驳回上诉，维持原判。</p> \n<p>　　<strong>法条链接</strong></p> \n<p>　　<strong>私自开拆、隐匿邮件罪</strong></p> \n<p>　　私自开拆、隐匿、毁弃邮件、电报罪，是指邮政工作人员私自开拆或者隐匿、毁弃邮件、电报的行为。根据刑法第253条规定：邮政工作人员私自开拆或者隐匿、毁弃邮件、电报的，处二年以下有期徒刑或者拘役。犯前款罪而窃取财物的，依照本法第二百六十四条的规定定罪从重处罚。</p> \n<p>　　来源：法制晚报</p> \n<p class=\"article-editor\">责任编辑：李鹏 </p>', '2017-04-05 16:46:11', '（法制晚报记者洪雪吴洁编辑吴洁）安徽一投递员鲍某，打起用自己天天经手的信件换钱的主意。为此，他专门私拆寄给县政府、县委和纪检等部门的举报信件，不仅如此，鲍某还将3封举报县教育局副局长和7封举报县法院副院长的信件提供给被举报人以换取钱财。深读', '0', null, null, '1', '0', '安徽,纪委,信件', '2017-04-05 16:46:11', '1', null, null, '0', null, 'noarchive', '安徽一邮递员专拆纪委信件 给被举报人报信换钱', null, '1');
INSERT INTO `tz_content` VALUES ('105', 'keke', null, null, '<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/transform/20170405/rRCZ-fycwyph2052370.jpg\" alt=\"教宗方济各 资料图\" data-link=\"\" />\n <span class=\"img_descr\">教宗方济各 资料图</span>\n</div> \n<p>　　[环球网综合报道]美国联邦检方日前发表声明指出，新泽西州一名青少年已承认他企图对恐怖分子提供物资支援，以暗杀2015年前往宾州费城主持公开弥撒的教宗方济各。媒体报道指出，这名青少年是受到极端组织“伊斯兰国”（IS）启发。</p> \n<p>　　台湾“中央社”4月5日报道称，17岁的柯隆（Santos&nbsp;Colon）昨天在新州康敦（Camden）的联邦法院认罪，表示他企图与一名狙击手共谋，在教宗方济各（Pope&nbsp;Francis）造访费城时射杀他，以及在周遭地区引爆炸药装置。</p> \n<p>　　检方表示，柯隆以为他从6月30日至8月14日合作的人是狙击手，但实际上是美国联邦调查局（FBI）卧底人员。这起攻击未成真，柯隆2015年遭FBI干员逮捕。</p> \n<p>　　报道称，柯隆被控告犯下企图对恐怖分子提供物资支援罪，最高恐面临15年有期徒刑。</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:11', '[环球网综合报道]美国联邦检方日前发表声明指出，新泽西州一名青少年已承认他企图对恐怖分子提供物资支援，以暗杀2015年前往宾州费城主持公开弥撒的教宗方济各。媒体报道指出，这名青少年是受到极端组织“伊斯兰国”（IS）启发。台湾“中央社”4月5', '0', null, null, '1', '0', '方济各,攻击,台湾', '2017-04-05 16:46:11', '1', null, null, '0', null, 'noarchive', '17岁少年找人密谋射杀教宗方济各 错找FBI卧底', null, '1');
INSERT INTO `tz_content` VALUES ('106', 'keke', null, null, '<p>　　[环球时报综合报道]台当局4日公布蔡英文访问拉美四国的详细行程，试图为其“冲喜”，但无人喝彩。蔡英文迫切需要“喜事”来提振士气，2017开年以来，多份民调都显示，她的民意支持率急剧跌落，而从去年5月20日风光上台至今才不过8个月。连日来，“一例一休”、同性婚姻、物价飞涨搅得岛内鸡犬不宁，与特朗普打电话后引发的“台湾筹码论”、丢失“邦交国”等也使得她灰头土脸。</p> \n<p>　　“英捷专案”，台当局特意给蔡英文此次出访起了个吉利的名字，寓意“蔡英文大捷”，但现在看上去阴云笼罩。亲绿的《自由时报》4日称，大陆上月挖走台“友邦”圣多美和普林西比，作为对“川蔡通话”的报复，是否在蔡英文出访时再度“出手”，备受关注。根据通报，蔡英文的行程为7日-15日，其间过境美国旧金山与休斯敦，这两个城市与纽约的距离都超过2000公里。此前曾有猜测她或许过境纽约，与美国当选总统特朗普见面。</p> \n<p>　　岛内媒体鲜有为蔡英文此行兴奋，“东森新闻云”网站主页上，甚至看不到这则新闻，该网站突出报道的是“鸡排涨到70元”“娶同学妈妈”“瑞典皇宫闹鬼”等消息。</p> \n<p>　　连日来岛内的热点新闻是有关蔡英文与民进党的各种新年民调。国民党文传会3日公布委托民调公司进行的支持度调查，结果显示，对蔡英文担任“总统”的表现，认为不满意的从去年10月14日调查的48.3%上升到55.3%；认为满意的则从42.8%下降到36.9%，国民党还夺下29.9%的支持度，胜过民进党的26.3%。“东森新闻”称，对此，民进党发言人张志豪的回应是“看看就好”。其网站上，网民邱世勋质问：用爱发电，用心诈骗，上任到现在有什么实质作为吗？《醒报》3日引述专家的话称，执政蜜月期已过，蓝绿两党都会回归到支持基本盘，未来民进党支持者差不多会有30%，国民党是27%-30%。</p> \n<p>　　同在3日，岛内的《天下杂志》发表民调结果称，对蔡英文的表现，只有34%的满意度，不太满意以及非常不满意的比例蹿升到56.1%，离岛地区的满意度甚至只剩9.7%！岛内一位资深媒体人4日对《环球时报》记者表示，了解台湾政治板块的人都知道，东部的宜兰向来是绿营的“民主圣地”，而离岛人最多的澎湖近年也是绿营执政，这些算是“绿营基本盘”所在。</p> \n<p>　　“2016年觉得最后悔的事是？”台湾最大网络论坛PTT网站上周以此进行话题民调，在2万多人参与的投票中，“投给民进党”高居榜首，比例超过59%，接下来才是“告白”“分手”“换工作”等。TVBS同期的民调结果显示，对台湾未来的整体发展，只有34%的人表示乐观，在两岸关系上仅20%的民众感到乐观。“番薯藤”新闻网3日称，民众看坏两岸关系的程度已超过陈水扁政府时期。</p> \n<p>　　台大政治系学者彭锦鹏4日对《旺报》说，蔡英文的满意度只剩不到4成，可见当初支持她当选的中间选民已崩盘，只剩民进党的铁杆粉丝，蔡政府上任后所有政策措施打击面太大，不管是年金改革、两岸关系、经济面、产业面、用人等，都显示施政无方、用人不当，所以民调下降是必然的。</p> \n<p>　　4日接受《环球时报》采访时，台湾大学政治学系教授、孙文学校校长张亚中表示，对岛内出炉的各个民调，最实质的信息是民众对民进党期望值急剧下滑，这是确定的。他认为，如果蔡英文执政保持这样的势头，2020年民进党不会让她继续参选，也就是说蔡英文可能一任就到头了，“蔡英文本来在民进党就不是一个实权派，而是各方妥协的产物，顺利时大家都拍巴掌，一旦不顺利就会有很多人落井下石”。</p> \n<p>　　据台湾《联合晚报》3日报道，为了让蔡英文政府过去几个月来的施政不留白，“行政院”特别整理出192项政绩。报道不无嘲讽地说，“蔡英文政府上任才229天，扣除68天假日，实际上班仅仅161天，竟然就盘整出192项重大法案及政策，可说是一天超过一政绩”。</p> \n<p>　　[环球时报驻台北特约记者 萧师言 环球时报记者 崔杰通 汪析]</p> \n<p class=\"article-editor\">责任编辑：刘德宾 SN222</p>', '2017-04-05 16:46:11', '[环球时报综合报道]台当局4日公布蔡英文访问拉美四国的详细行程，试图为其“冲喜”，但无人喝彩。蔡英文迫切需要“喜事”来提振士气，2017开年以来，多份民调都显示，她的民意支持率急剧跌落，而从去年5月20日风光上台至今才不过8个月。连日来，“', '0', null, null, '1', '0', '蔡英文,执政危机', '2017-04-05 16:46:11', '1', null, null, '0', null, 'noarchive', '台学者称蔡英文支持率已崩盘 民意洪水引发危机', null, '1');
INSERT INTO `tz_content` VALUES ('107', 'keke', null, null, '<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/crawl/20170405/4LyD-fycxmks5652690.jpg\" alt=\"□　本报记者　　　余东明\" />\n</div> \n<p>　　近日，上海市浦东新区人民检察院办理了一起非法经营案，被告人王某和卓某等人在两年内通过淘宝、QQ等平台对外非法销售隐形眼镜及护理液。</p> \n<p>　　根据国家相关规定，隐形眼镜及护理液属于三类医疗器械，必须获得该类产品医疗器械注册证书以及相应生产、经营资质证书才能进行生产经营。然而，在暴利的诱惑下，即使没有相关证书，王某、卓某等人仍选择铤而走险。</p> \n<p>　　2016年7月29日，公安机关在卓某等人租借的仓库中查获隐形眼镜114万余片，护理液13万余瓶，涉案物品价值达2796万元人民币。</p> \n<p>　　卓某在被警方抓获后，坦白了其从事隐形眼镜非法经营中的利益链：他们进口的主要品牌的进价在0.6美元到1.05美元之间，折合人民币约4元到7.2元；而售价则为45元至100元人民币不等。</p> \n<p>　　记者经过计算发现，其中的差价在10倍以上。</p> \n<p>　　“我所销售的产品没有中文标签、注册证编号，是不能在大陆销售的。”被告人卓某对于自己的不法事实供认不讳。</p> \n<p>　　卓某对于合法销售隐形眼镜的几个必要条件相当熟知：“国内销售隐形眼镜，需要《医疗器械经营企业许可证》、三级资质，还要在国家食药监局备案，获得国食药监械（进）字的注册号。”</p> \n<p>　　卓某等人是如何运作的呢？根据卓某的交代和警方的调查，记者掌握了如下情况：首先，卓某等人先从国外进货。</p> \n<p>　　他交代说：“neo（隐形眼镜品牌）是从英国进口的，从英国运到上海港，再从海关物流运到我的公司；bebe（隐形眼镜品牌）等隐形眼镜是通过韩国发往香港，再从香港发到上海。”为了通关，卓某和多家医疗器械公司签订了协议，借用他们的许可证。</p> \n<p>　　在销售方面，卓某供述称，其在天猫上注册了一家店铺。卓某等人设置了障眼法，以此来规避天猫方面对医疗器械销售的监管。</p> \n<p>　　据记者了解，他们通过QQ、百度搜索等平台招聘代理，即所谓的下家。下家通过批发，从而赚取中间差价。</p> \n<p>　　李某就是卓某发展的其中一名下家。李某交代称，她销售的方式相对“与众不同”。她先在微博上让所谓的“网红”“大咖”为产品做广告，客户决定进入购买页面，李某就让他们通过微信、支付宝等下单并付款。收到钱后，她再到卓某的网店里直接下单，产品会发往顾客提交的地址。根据李某交代，她在2015年7月到2016年7月的一年里，通过此种下单方式购买隐形眼镜及护理液的金额总计21万余元。</p> \n<p>　　浦东新区检察院认为，卓某、王某、李某3人违反医疗器械经营相关管理规定，从事非法经营活动，应该追究刑事责任并以涉嫌非法经营罪提起公诉。</p> \n<p>　　来源：法制日报</p> \n<p class=\"article-editor\">责任编辑：张迪 </p>', '2017-04-05 16:46:11', '近日，上海市浦东新区人民检察院办理了一起非法经营案，被告人王某和卓某等人在两年内通过淘宝、QQ等平台对外非法销售隐形眼镜及护理液。根据国家相关规定，隐形眼镜及护理液属于三类医疗器械，必须获得该类产品医疗器械注册证书以及相应生产、经营资质证书', '0', null, null, '1', '0', '隐形眼镜,进口,利润,走私', '2017-04-05 16:46:11', '1', null, null, '0', null, 'noarchive', '进口隐形眼镜黑色网络销售链:利润高达10余倍', null, '1');
INSERT INTO `tz_content` VALUES ('108', 'keke', null, null, '<p>　　据广东省公安厅通报，今年1-10月全省盗抢骗案件发案数同比下降20.6%，公安机关刑拘4.65万人</p> \n<p>　　一盗窃团伙招募拐骗未成年人，装扮成学生模样甚至背着书包，尾随业主混入小区实施入室盗窃；一电信诈骗团伙在省内外多地招募人员设立电话窝点从事电信诈骗犯罪活动，并直接通过POS机将被骗资金进行转移。</p> \n<p>　　据广东省公安厅昨天通报，今年1-10月全省盗抢骗案件发案数同比下降20.6%，各类多发性侵财犯罪发案得到了有效的遏制；全省共侦破盗抢骗案件131712起，刑拘盗抢骗犯罪嫌疑人4.65万人。</p> \n<p>　　■采写：新快报记者 陈海生 通讯员 刘辉 黄桂林</p> \n<p><strong>　　典型案例</strong></p> \n<p><strong>　　1</strong></p> \n<p><strong>　　借助互联网选目标 疑犯穿校服进小区</strong></p> \n<p>　　一团伙招募拐骗未成年人，传授盗窃方法以及盗窃后销赃分配；实施作案由团伙成年骨干成员带队，带领2名或2名以上未成年成员前往目标城市进行入室盗窃；这 些未成年人往往装扮成学生模样甚至背着书包，尾随业主混入小区，使一些中高档小区严格的保安盘查防不胜防。近日，佛山警方成功打掉一个清远英德籍操纵未成 年人跨区域流窜入室盗窃犯罪团伙，抓获团伙11名，核破广东、江苏、浙江、山东、河南、安徽、湖南等7省攀爬入室盗窃案件110余起。</p> \n<p>　　经过对范荣锦等4名犯罪嫌疑人初审，该团伙流窜全国各地疯狂作案，且组织带头的成年疑犯多不出现在作案现场，而是指令一伙未满刑事责任年龄人员实施作案。</p> \n<p>　　警方介绍，该团伙特点鲜明：该团伙有组织有预谋。团伙内部由成年人控制，组织严密，分工明确。从抓获人员审讯情况推断，团伙头目不离开广东，主要负责招募 拐骗未成年人，传授盗窃方法以及盗窃后销赃分配；实施作案由团伙成年骨干成员带队，带领2名或2名以上未成年成员前往目标城市进行入室盗窃。</p> \n<p>　　警方介绍，该团伙还有专业化流窜化的特点。团伙作案准备充分，由团伙主犯在广东英德当地物色培养未成年作案者，随后驾车或坐车带领未成年人前往选好的目标 地，包括浙江、江苏等省以及广东省广州、佛山、中山、东莞等地，通过互联网或实地踩点选定作案目标，得手后返回广东销赃后由头目分配赃款，大部分被头目和 骨干成员分享，未成年嫌疑人只能维持基本生活所需。</p> \n<p><strong>　　2</strong></p> \n<p><strong>　　打击电信诈骗团伙 兵分五路抓获51人</strong></p> \n<p>　　11月27日，广东省公安厅刑侦局组织茂名市及相关地市公安机关成功侦破“3&middot;19猜猜我是谁”特大跨区域电信诈骗团伙专案，抓获嫌疑人51名，刑拘24 人，串并全国各地案件45宗（涉及北京、上海、江苏、浙江、福建、黑龙江、安徽、河南、新疆等地案件31宗），涉案金额达126.9万元。</p> \n<p>　　今年9月份，省公安厅刑侦局组织茂名市、电白区公安机关开展“拔钉子”专项行动中，发现一条疑似“猜猜我是谁”电信诈骗犯罪团伙组织者的重要线索。经深入分析排查，专案组发现该诈骗团伙在云南楚雄、广东清远、广州增城以及电白农村等省内外地区设立诈骗电话窝点。</p> \n<p>　　2015年11月27日凌晨，指挥部指令茂名市公安机关抽调刑警、巡警、特警等多警种共250名警力兵分五路奔赴云南楚雄、湖北武汉及广东清远、广州增城等地及茂名市境内的21个目标点开展统一收网行动。</p> \n<p>　　行动中，共抓获了包括主犯莫某超在内的电信诈骗犯罪嫌疑人51名，刑拘24人，当场缴获作案手机108部、手机卡118张、银行卡64张、POS机7台、公民信息14600多条。</p> \n<p><strong>　　数据</strong></p> \n<p><strong>　　今年头10个月 刑拘盗抢骗疑犯4.65万人</strong></p> \n<p>　　据通报，今年1-10月，全省盗抢骗案件发案数同比下降20.6%，各类多发性侵财犯罪发案得到了有效的遏制；全省共侦破盗抢骗案件131712起，刑拘 盗抢骗犯罪嫌疑人4.65万人，检察机关批准逮捕3.31万人，起诉3.09万人，起诉各类跨市、跨省、系列性、团伙性盗抢骗犯罪团伙498个涉及案件 10798起，省公安厅挂牌督办的72起重点案件已侦破其中67起，侦破率为93%，100名督捕逃犯抓获其中81名，30名公开悬赏逃犯抓获其中14 名。</p>', '2017-04-05 16:46:11', '据广东省公安厅通报，今年1-10月全省盗抢骗案件发案数同比下降20.6%，公安机关刑拘4.65万人一盗窃团伙招募拐骗未成年人，装扮成学生模样甚至背着书包，尾随业主混入小区实施入室盗窃；一电信诈骗团伙在省内外多地招募人员设立电话窝点从事电信诈', '0', null, null, '1', '0', '入室盗窃,未成年人', '2017-04-05 16:46:11', '1', null, null, '0', null, 'noarchive', '团伙招未成年人扮学生入室盗窃 流窜七省作案', null, '1');
INSERT INTO `tz_content` VALUES ('109', 'keke', null, null, '<p>　　原标题：韩国总统候选人全部敲定 安哲秀缩小差距文在寅呼声最高</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/transform/20170405/v_BC-fycwymx3769817.jpg\" alt=\"安哲秀。资料图\" data-link=\"\" />\n <span class=\"img_descr\">安哲秀。资料图</span>\n</div> \n<p>　　[环球时报驻韩国、日本特约记者 李军 李珍]4日，随着韩国国民之党前党首安哲秀以75.1%的得票率成功当选该党总统候选人，韩国国会各政党总统候选人全部敲定。第19届韩国总统将在共同民主党的文在寅、国民之党的安哲秀、自由韩国党的洪准杓、正党的刘承旼以及正义党的沈相奵五人之中产生。</p> \n<p>　　在此前的共同民主党党内竞选中，文在寅累计得票率为57.0%，力压城南市长李在明和忠清南道知事安熙正成为该党下届大选候选人。文在寅在3日的演讲中表示，“今天我将开始书写大韩民国全新的国民历史，（我将带领韩国）与分裂的时代说再见，朝着正义统一的新时代迈进”。</p> \n<p>　　而位居民调支持率第二位的安哲秀2012年曾以无党派身份参选第18届总统，最终在与在野的民主统合党合推单一候选人的过程中不敌文在寅，而后者最终在与朴槿惠的角逐中败北。安哲秀4日在党内胜选演讲中表示，“分裂主义、霸权主义创造不了国家的未来，（我）要聚合平凡国民的力量，创造一个非凡的大韩民国，要成为一位为了国民、依赖国民的国民总统”。</p> \n<p>　　目前的执政党自由韩国党3月31日举行全体党员大会，庆尚南道知事洪准杓以54.1%的得票率被推选为该党总统候选人。洪准杓在2007年曾参加当时执政的大国家党（自由韩国党前身）党内竞选，在前总统李明博和朴槿惠两大有力竞争者的“围攻”下败北。今年，在代总统黄教安宣布不参选总统后，洪准杓成为自由韩国党内呼声最高的人选。</p> \n<p>　　代表韩国保守阵营“正党”正式参加总统选举的刘承旼一直以来强调成立“反文（在寅）联盟”来坚守保守路线。刘承旼表示，他将成为保守阵营新的希望，承载着国民对保守阵营重建的期待，在大选中获得国民支持。</p> \n<p>　　韩国正义党党首沈相奵自今年2月就表明参选第19届总统的意向，但由于势单力薄，即使单独参选也难以在大选中创造奇迹。</p> \n<p>　　《韩民族日报》4日报道称，五大政党角逐下届总统大选的竞选格局已出炉，尽管各候选人均表态力逐总统“宝座”，但5月9日的大选是在多方角逐中进行，还是纵横捭阖形成两强对决，目前还尚难确定。根据韩国选举制度，只有在大选中得票率达到15%以上的候选人，其竞选费用才能得到保全。从目前来看，仅有文在寅和安哲秀的支持率在15%以上，而支持率较低的候选人很可能因竞选资金压力等因素而中途退选。</p> \n<p>　　据韩国JTBC电视台4日委托韩国调查公司以1000名选民为对象所进行的紧急舆论调查结果显示，共同民主党候选人文在寅和国民之党候选人安哲秀分别以39.1%和31.8%位居五位候选人的第一和第二，而自由韩国党候选人洪准杓、正党候选人刘承旼和正义党候选人沈相奵的支持率分别为8.6%、3.8%、3.7%。特别是随着安哲秀近期支持率的急剧上升，其支持率不仅首次突破30%，而且与一直排在第一位的文在寅仅相差仅7%。《东亚日报》3日公布的民调结果显示，文在寅（41.7%）与安哲秀（39.3%）的支持率已经缩小到误差范围以内。</p> \n<p>　　在韩国各政党敲定总统候选人的背景下，4日晚，日本驻韩国大使长岭安政从东京乘飞机抵达首尔，时隔85天后重回驻韩大使的岗位。日本《每日新闻》报道称，4日上午，首相安倍晋三在首相官邸同长岭安政进行会谈，并指示长岭要做好韩国政权交接期间的准备工作，同时要让韩国现政权遵守日韩共识。《读卖新闻》称，安倍还要求长岭对韩国方面做工作，让其撤去象征慰安妇的少女像。长岭在会后对记者表示，“正在协调和（黄教安）代总统直接对话”。</p> \n<p>　　2015年12月29日，日韩两国在慰安妇问题上达成共识。日方当时承诺出资10亿日元援助慰安妇受害者，韩方则表示要在撤除慰安妇少女雕像一事上“努力”。2016年12月31日，韩国民间团体在釜山的日本驻韩总领事馆门前竖起象征慰安妇的少女雕像，引发日本政府强烈抗议，随后于今年1月9日召回日本驻韩大使长岭安政。</p> \n<p>　　“没有取得任何成果就‘放下了举起的拳头’”，共同社4日引述日本政府人士的话称，日本政府担忧若继续摆出强硬姿态，将与5月选出的韩国新政府从一开始就保持对立。日媒分析称，在韩国下届总统选举候选人当中，共同民主党候选人文在寅目前的支持率最高。文在寅在竞选演讲中曾表示“要废除”日韩慰安妇问题共识。日本政府认为，有必要从韩国总统选举期间就开始对文在寅方面做工作。</p> \n<p class=\"article-editor\">责任编辑：李伟山 </p>', '2017-04-05 16:46:11', '原标题：韩国总统候选人全部敲定安哲秀缩小差距文在寅呼声最高[环球时报驻韩国、日本特约记者李军李珍]4日，随着韩国国民之党前党首安哲秀以75.1%的得票率成功当选该党总统候选人，韩国国会各政党总统候选人全部敲定。第19届韩国总统将在共同民主党', '0', null, null, '1', '0', '大选,韩国', '2017-04-05 16:46:11', '1', null, null, '0', null, 'noarchive', '韩总统候选人敲定 安哲秀缩差距文在寅呼声最高', null, '1');
INSERT INTO `tz_content` VALUES ('110', 'keke', null, null, '<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/crawl/20151214/qkRx-fxmpnqm3214493.jpg\" alt=\"\" />\n <span class=\"img_descr\">&nbsp;&nbsp;&nbsp;&nbsp;▲家属给符官洪（右一）送来锦旗表示感谢</span>\n</div> \n<p>　　垫江县八旬老人杨文俊独自去吃宴席，哪晓得就此失踪了。6天后，一名司机去爬山，不慎滚落荒山，赫然发现山下竟然有个摔伤昏迷的老人。老人就是失踪6天的杨文俊。</p> \n<p><strong>　　司机滚落山坡</strong></p> \n<p>　　杨文俊今年80岁，走失那天是12月3日。当天，他从垫江县杠家镇家里出发去县城吃宴席，家人后来发现他没有出现在宴席上，当晚也没有回家。</p> \n<p>　　家人发动所有亲友到老人可能去的地方找了个遍，无功而返。接到报警的消防和公安等部门也多方找寻，仍是一无所获。</p> \n<p>　　12月9日，老人失踪第7天，垫江县公交公司驾驶员符官洪，趁着休息时间与朋友一道到邱家沟水库附近爬山。</p> \n<p>　　下午5点多钟，两人匆匆抄捷径下山。符官洪不小心脚下踩滑，从山坡上滚落，摔到坡底草丛中。停止翻滚后，他感觉身体压到了一个软绵绵的东西。</p> \n<p><strong>　　发现失踪老人</strong></p> \n<p>　　朋友赶紧下到坡底，他俩小心翼翼拨开草丛，吓出一身冷汗：一个老人躺在草丛里。</p> \n<p>　　符官洪立即向110求助。桂阳派出所民警很快到达现场。荆棘密布，山路陡峭，加上老人身体虚弱不能行走，民警向消防求助。</p> \n<p>　　消防官兵带上专业救援工具赶到现场，120急救车同时到达邱家沟水库附近待命。经过艰难救援，晚上9点多钟，老人被送到医院。</p> \n<p>　　医院检查发现，老人颅内出血，处于半昏迷状态，经过抢救保住了性命。医生通过老人胃肠道和生命体征判断，他至少3天没有进食了。</p> \n<p>　　事后警方调查确认，获救老人正是此前失踪的杨文俊。</p> \n<p>　　医生分析，老人虽然长时间未进食，但因为处于昏迷状态，体能消耗小，加上事发地草丛深干草多，起到了保暖作用，因此得以存活。</p> \n<p><strong>　　婉拒万元酬谢</strong></p> \n<p>　　昨日，经过几天治疗，已经清醒过来的杨文俊被家人接回家。</p> \n<p>　　老人出院后，杨文俊的儿子杨光辉去派出所、消防队、公交公司送锦旗致谢。在公交公司，他掏出1万元现金送给符官洪，感谢救命之恩。符官洪说什么都不肯收。</p> \n<p>　　杨光辉愧疚地说，父亲失踪和他们疏于照顾有直接关系，“今后，我们一定要加强对他的看护，再不让他单独外出。”</p> \n<p>　　因为摔伤导致颅内出血，杨文俊部分记忆丧失，他只记得到县城后找不到吃宴席的地方，就在街边饭馆吃了午饭准备坐车回家，糊里糊涂迷路了，不知怎么走到摔倒的地方的。摔下坡后，他隐约记得自己还喝过几次露水。</p> \n<p>　　（重庆晚报24小时新闻热线966988感谢垫江论坛提供线索和图片）</p> \n<p>　　重庆晚报首席记者&nbsp;夏祥洲</p>', '2017-04-05 16:46:12', '垫江县八旬老人杨文俊独自去吃宴席，哪晓得就此失踪了。6天后，一名司机去爬山，不慎滚落荒山，赫然发现山下竟然有个摔伤昏迷的老人。老人就是失踪6天的杨文俊。司机滚落山坡杨文俊今年80岁，走失那天是12月3日。当天，他从垫江县杠家镇家里出发去县城', '0', null, null, '1', '0', '荒山,老人坠崖', '2017-04-05 16:46:12', '1', null, null, '0', null, 'noarchive', '八旬老人滚落荒山 7天后被失足爬山者救出', null, '1');
INSERT INTO `tz_content` VALUES ('111', 'keke', null, null, '<p>　　原标题：雄安新区单身概念也遭炒作？ 雄县民政局：近期结婚数量无特别变化</p> \n<p>　　近期，有网络段子称雄安新区单身男女成为争抢对象，对此，今日上午记者来到雄县民政局询问情况。</p> \n<p>　　每经记者 苏杰德 张虹蕾 摄影报道 每经编辑 宋思艰</p> \n<p>　　随着设立雄安新区消息的持续发酵，除了火热的房产市场，也出现了一些真假难辨却被津津乐道的网络段子。其中一则是所谓的雄安新区征婚启事，“男，53岁，离异，无孩，农户，在雄县有两亩地，希望女方25岁以下，英美留学优先。”</p> \n<p>　　也有网络消息称，“听说目前最火爆的雄安新区消息不是找房子找地皮，因为政府全部停止处理，最火爆生意是婚介所，以前被冷落的单身男女现在都是抢夺对象”。</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/crawl/20170404/tbIq-fycxmks5569968.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　为查证上述传闻情况，今日（4月4日）上午，《每日经济新闻》记者来到河北省保定市雄县民政局，询问最近的结婚登记情况。民政局相关工作人员对记者称，最近（清明节前）结婚人员数量正常，没有特别变化；清明节期间民政局并没有上班。当被记者问及结婚登记是否会受限制时，该工作人员称，具体情况尚不清楚，尚无新政策。</p> \n<p class=\"article-editor\">责任编辑：张迪 </p>', '2017-04-05 16:46:12', '原标题：雄安新区单身概念也遭炒作？雄县民政局：近期结婚数量无特别变化近期，有网络段子称雄安新区单身男女成为争抢对象，对此，今日上午记者来到雄县民政局询问情况。每经记者苏杰德张虹蕾摄影报道每经编辑宋思艰随着设立雄安新区消息的持续发酵，除了火热', '0', null, null, '1', '0', '民政局,结婚,雄安新区', '2017-04-05 16:46:12', '1', null, null, '0', null, 'noarchive', '雄安单身人被争抢? 民政局:近期结婚数量正常', null, '1');
INSERT INTO `tz_content` VALUES ('112', 'keke', null, null, '<div class=\"img_wrapper\"> \n <img alt=\"戴建华受审\" src=\"http://n.sinaimg.cn/news/transform/20170404/mmvv-fycwyns4644434.jpg\" /> \n <span class=\"img_descr\">戴建华受审</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"马超群上了中纪委专题片\" src=\"http://n.sinaimg.cn/news/transform/20170404/_epQ-fycwyns4644438.jpg\" /> \n <span class=\"img_descr\">马超群上了中纪委专题片</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"汤少波\" src=\"http://n.sinaimg.cn/news/transform/20170404/Lh0b-fycxmks5530642.jpg\" /> \n <span class=\"img_descr\">汤少波</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"符涛生\" src=\"http://n.sinaimg.cn/news/transform/20170404/qdPa-fycwymx3636646.jpg\" /> \n <span class=\"img_descr\">符涛生</span> \n</div> \n<p>　　原标题：揭秘反腐新书中“有原则”贪官：一般人的钱我不收</p> \n<p>　　（法制晚报记者张莹编辑岳三猛）近日，因正部级落马高官自述与女明星情史，口述体反腐纪实文学《追问》走红。</p> \n<p>　　先期拿到该书的观海解局（微信ID：guanhaijieju）记者发现，其中有个叫李立青（化名）的市委副书记很有意思：他只收熟人的钱，一般人的钱物都退了，以至于被双规之前还被认为是个清正廉明的干部。</p> \n<p>　　其实，这种自诩很有“原则”的清官伪装者并不少，个别甚至搞出“三个不收”：数额太大的、关系不太熟的、看不顺眼的，统统不收。</p> \n<p>　　对于这种有“鸵鸟心理”、试图确保安全的熟人贿赂，专家建议进一步厘清权力边界，净化畸形的官员“朋友圈”。</p> \n<p><strong>　　贪官自述：一般人的钱不会要</strong></p> \n<p>　　3月23日晚，由中央党校出版社出版的口述体反腐纪实文学《追问》，举行新书发布会。作者为中国作协会员丁捷，他还是江苏省属某文化单位的纪委书记。</p> \n<p>　　3月25日，丁捷在做客观海解局（微信ID：guanhaijieju）直播间，接受《玉言》栏目采访。他从中纪委和省纪委提供的633个案例中，遴选出28个以上地厅级与省管干部违纪违法典型，最后成功与他们接触。与其中13人面对面长时间交谈，获得了数十万字关于他们人生道路、心灵历程和灵魂语言的第一手材料。最后，又从中选择8位典型，进行深度记述。</p> \n<p>　　在本书的第二部分《无法直立》中，丁捷写到了这样一名落马干部：某个市的政协主席李立青（正厅级）在担任市委常委、组织部长和市委副书记的6年时间里卖官卖爵，并为煤矿老板、开发商等大开方便暴富之门，伙同妻子、女婿等亲属受贿4600余万元，最终被判处无期徒刑。</p> \n<p>　　在接受采访时，李立青说他和妻子只收一个叫许博士及其关系户的钱，不扩大范围。“一般别人来找我办事，给点香烟给点酒给几张衣服券超市卡什么的，我几乎都退了。给我钱，我更是不会要。”至于原因，他表示，自己不想为这些小恩小惠替人家做事，还弄得满城风雨一身贪名。</p> \n<p>　　至于商人许博士，他的定义是“自己人”——后者不但幕后操作帮时任教师的李立青成功谋到市府秘书处干部一职，还进一步将李立青推到正厅级职务上。于是，许博士趁着李立青女儿结婚时，一把送上888万元，后者也就笑纳了。拿人钱财替人消灾，此时的李立青就变成了这个商人的木偶，安排诸多他推荐的人进入领导岗位。</p> \n<p>　　可笑的是，表面清正廉洁、好干部的李立青之所以倒下，正是因为许博士的举报。后者觉得李立青拿了钱却不听话，推荐个干部常常不爽快安排，就不高兴了，设计一个买官圈套令其落马。</p> \n<p>　　其中一个细节是，当李立青风闻自己被调查时，找到行贿近2000万的许博士，要求其就说两人只有几顿饭的来往。结果对方没等他说完就大声开骂：“姓李的，去你妈的，你当我跟你一样弱智啊！”</p> \n<p><strong>　　贪官“讲原则”：不办事不收钱</strong></p> \n<p>　　观海解局（微信ID：guanhaijieju）记者发现，不少贪官和《追问》所记述的李立青有着类似的特点：利益交换总是限定在周围一小撮非常熟的“朋友”中，显得受贿很有“原则”。其中一个很重要的原因是，他们掩耳盗铃，妄图将人情当成一张“遮羞布”。</p> \n<p>　　据《浙江日报》报道，2012年，温州市房产管理局龙湾分局原局长戴建华走上法院的被告席。据披露，戴建华第一次收钱，就是来自他的一位哥儿们韩某。他帮后者顺利租到店面，比市场价便宜很多。事后，韩某给戴建华送去1万元，戴建华觉得这是朋友间的相互帮助，就欣然收下。</p> \n<p>　　接下来几年内，戴建华又利用职权“热心”的帮助了几位朋友，例如：将装修工程承包给熟人、“帮忙”酒店摆平违规装修问题、帮助违章的建筑得到安置费用等，并在事后得到朋友的感谢费。</p> \n<p>　　与戴建华类似，南京溧水原副县长汤少波也只收熟人的钱，而且更为小心谨慎。有三种情况他是不会收受贿赂的：一是数额太大的不收；二是关系不太熟的不收；三是看不顺眼的不收。因此汤少波有了“三不干部”之称。</p> \n<p>　　2008年6月，浙江一个做汽车零配件生意的老板，想在溧水做些项目，经人介绍认识了汤少波。在接触过程中，该老板送给汤少波一个纸袋，里面除了香烟外还有1万美元。汤少波发现后，立即把1万美元交给了开发区纪工委。“与该老板关系不是很熟，不是很放心，而且1万美元数额太大。” 汤少波说。</p> \n<p>　　有些老板摸透了汤少波的心理，尽量把送钱的事做得比较随意，数额也不是太大，每次两三万元，让汤少波感觉不到是在行贿。实际上，汤少波所收受的77万元财物，也是4家公司8年共27次送给他的。</p> \n<p>　　这种“讲原则”的贪官，虽然少见，却也并不是孤例。据《中国纪检监察报》报道，类似的还有广东省韶关市公安局原局长叶树养有“五不”原则（不主动索要钱财、不办事不收钱、不催讨该给还没给的钱、不讨价还价、不介意钱多钱少），黑龙江省绥棱县委原书记李刚有“三不收”原则（事没办成的不收、关系不密切的不收、几个人同时送的不收），河北省石家庄市交通局原副局长杜国栋也有“就是不能让单位吃亏，只收干活干好的，干差的送钱也不收”的原则。</p> \n<p><strong>　　河北“亿元巨贪”谁的钱都敢收</strong></p> \n<p>　　观海解局（微信ID：guanhaijieju）记者发现上述“讲原则”、只收熟人贿赂的贪官只是少数，大多数还是“来者不拒”。这些人在钱财的来源、金额上没有过多的顾忌，有人甚至恬不知耻地在法庭上辩称“想说不收真的很难”、“办事不给回报不舒服”。</p> \n<p>　　据《法制日报》报道，去年5月26日，海南文昌原副市长符涛生涉嫌受贿案一审开庭。据检方指控，符涛生利用职务便利，在民生工程承建承揽等方面为他人谋利，多次收受承包人符某等12人贿赂共计239.1万元。其中，收回数额每次从数千元到几十万元不等。</p> \n<p>　　面对检方指控，符涛生表示认罪服法，但辩称自己系“被动型”受贿，收钱也很无奈，“开发商们个个腰缠万贯，个个是款爷。这些工程是我帮他们拿到的，他们发财吃肉时，自己跟着喝些汤也在情理之中……”</p> \n<p>　　安徽滁州市琅琊区政协原主席孙协也是对钱财来者不拒的典型，并且“办事不给回报就不舒服”。孙协接受调查期间曾透露，第一次接受项目经理丁某送给他的5000元感谢费的时候，“心里很是害怕，不踏实，还有些内疚感”。但随着次数越来越多，他开始对送来的钱物来者不拒，甚至发展到后期“办事不给回报就不舒服”。</p> \n<p>　　河北“亿元贪官”马超群则是他们中的“佼佼者”。据河北省纪委2014年11月通报，办案人员共在秦皇岛市城市管理局原副调研员、北戴河供水总公司原总经理马超群家中搜出现金1.2亿元，黄金37公斤，在北京和秦皇岛等地的房屋手续68套。</p> \n<p>　　据秦皇岛市当地一些干部群众反映，马超群作为当地供水公司领导，相当“贪婪跋扈”，他在当地人中的口碑“挺坏”，名声较差。“谁的钱他都要收，哪儿的钱都敢要。”一位熟悉马超群的当地干部反映，“不给钱就不给你通水，给钱少了就给你断水。”</p> \n<p><strong>　　应净化官员畸形“朋友圈”</strong></p> \n<p>　　对于部分官员只收取熟人贿赂，2015年12月，清华大学公共管理学院助理研究员李松峰在接受新华社记者采访时表示，官员“权力寻租”的欲望是形成官商“小圈子”的重要原因。“从实质上看，‘小圈子’是双方权益交换的媒介。通过这个圈子，官员私售权力，商人奉送钱财，各取所需。”</p> \n<p>　　李松峰表示，另一方面，“小圈子”的出现，也是一些官员“鸵鸟心理”作祟。由于“小圈子”内的成员经过官员精心选择，隐蔽性很强，这会使官员产生一种莫名的安全感，他们会觉得只要腐败局限在“小圈子”内，就不易被人发现。</p> \n<p>　　网络评论员乔志峰也有着相同的看法，他认为，这种采用选择性收钱的贪官，通常会坚持不放心的钱不收、收了钱之后一定会办事的方式。“贪官觉得，只有坚持这两点，才能在保证利益最大化的同时确保安全，让千辛万苦才得到的乌纱帽万无一失。”</p> \n<p>　　《中国纪检监察报》曾发表文章称，对官员而言，发生贪腐，最主要的原因是信仰缺钙、宗旨观念淡薄。交友不慎、抵不住诱惑，都是起次要作用的外因。更何况，贪官所谓的“朋友关系”，只不过是小人之交而已。</p> \n<p>　　要防止贪官通过“朋友圈”受贿，一方面，要把权力关进“笼子”里，消灭官场中形形色色的小圈子；另一方面，要进一步厘清权力边界，净化畸形的官员“朋友圈”。</p> \n<p class=\"article-editor\">责任编辑：陈琰 SN225</p>', '2017-04-05 16:46:12', '原标题：揭秘反腐新书中“有原则”贪官：一般人的钱我不收（法制晚报记者张莹编辑岳三猛）近日，因正部级落马高官自述与女明星情史，口述体反腐纪实文学《追问》走红。先期拿到该书的观海解局（微信ID：guanhaijieju）记者发现，其中有个叫李立', '0', null, null, '1', '0', '反腐,贪官,受贿', '2017-04-05 16:46:12', '1', null, null, '0', null, 'noarchive', '揭反腐新书中“有原则”贪官：一般人钱我不收', null, '1');
INSERT INTO `tz_content` VALUES ('113', 'keke', null, null, '<div> \n <p>　　中新网4月5日电 河南省沁阳市公安局官方微博今日发布消息称，接到网上“沁阳交警收黑钱”的举报后，沁阳市公安局高度重视，立即组织纪委、督察部门开展调查，待查证属实后进行严肃处理。</p> \n <p>　　沁阳市公安局介绍，目前，调查工作正在开展之中。</p> \n <div class=\"img_wrapper\">\n  <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/5cGM-fycxmks5731564.jpg\" />\n </div> \n <table style=\"padding-right: 10px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" align=\"left\"> \n  <tbody> \n   <tr> \n    <td>&nbsp;</td> \n   </tr> \n  </tbody> \n </table> \n <div>\n  &nbsp;\n </div> \n</div> \n<p class=\"article-editor\">责任编辑：李鹏 </p>', '2017-04-05 16:46:12', '中新网4月5日电河南省沁阳市公安局官方微博今日发布消息称，接到网上“沁阳交警收黑钱”的举报后，沁阳市公安局高度重视，立即组织纪委、督察部门开展调查，待查证属实后进行严肃处理。沁阳市公安局介绍，目前，调查工作正在开展之中。', '0', null, null, '1', '0', '收黑钱,交警,河南', '2017-04-05 16:46:12', '1', null, null, '0', null, 'noarchive', '河南沁阳警方回应交警收黑钱：查证属实后严处', null, '1');
INSERT INTO `tz_content` VALUES ('114', 'keke', null, null, '<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/8HhJ-fycwyph2067702.png\" alt=\"慰问信截图　\" />\n <span class=\"img_descr\">慰问信截图　</span>\n</div> \n<p>　　[环球网综合报道]朝鲜劳动党中央机关报《劳动新闻》5日报道称，朝鲜领导人就圣彼得堡恐怖袭击向俄罗斯总统弗拉基米尔•普京致慰问信。</p> \n<p>　　慰问信以外交礼节上的国家元首，朝鲜最高人民会议常任委员会委员长金永南的名义发出。</p> \n<p>　　金永南在慰问信中称，“向总统普京、袭击死难者及家属表示深切同情与慰问”，并希望他们能尽快从悲痛中走出来。</p> \n<p>　　据了解，莫斯科时间4月3日14点30分左右，圣彼得堡地铁“技术大学2号”站和“先纳亚广场”站之间发生爆炸。此外，由于在“起义广场”站及时发现一枚自制炸弹，从而成功避免了另一起爆炸事件。俄罗斯卫星通讯社5日报道称，据最新资料显示，恐袭造成14人死亡，49人仍在医院接受治疗。</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:12', '[环球网综合报道]朝鲜劳动党中央机关报《劳动新闻》5日报道称，朝鲜领导人就圣彼得堡恐怖袭击向俄罗斯总统弗拉基米尔•普京致慰问信。慰问信以外交礼节上的国家元首，朝鲜最高人民会议常任委员会委员长金永南的名义发出。金永南在慰问信中称，“向总统普京', '0', null, null, '1', '0', '朝鲜,普京,恐袭', '2017-04-05 16:46:12', '1', null, null, '0', null, 'noarchive', '朝鲜领导人就俄圣彼得堡恐袭案向普京表示慰问', null, '1');
INSERT INTO `tz_content` VALUES ('115', 'keke', null, null, '<p>　　作者：乔青水、宋颖洁</p> \n<p>　　社会救助制度是关乎我国民生发展大计的重要制度，但在我国城镇化建设过程中，社会救助制度改革的步伐滞后于人民生活水平提升的步伐，尤其是农村地区居民在享受社会救助上存在明显缺口。未来，我国将深入推进新型城镇化建设，因此社会救助制度也必须不断完善充实，提高城乡社会救助覆盖面，使社会救助达到城乡一体化和共享化。</p> \n<p>　　<strong>我国城镇化过程中社会救助问题的制度根源</strong></p> \n<p>　　对社会救助的思想认识存在误区。目前，我国政府及社会对社会救助制度的思想认识仍然存在一定的误区，在社会救助作用、目标和救助对象等方面未能全面地认识。这就导致了我国现有社会救助制度中的相关政策在制定时存在盲区，因而落实也较为困难。这样也就导致社会救助的兜底作用难以真正发挥好。</p> \n<p>　　对社会救助制度的法制规范存在疏漏。就目前来看，我国社会救助的法制规范明显滞后于社会救助事业本身的发展。首先，我国尚未正式形成一套有效的社会救助法制思想，政府对社会救助的认识也普遍局限在政府施予恩惠和开展慈善性事业，没有真正意识到社会救助是公民的基本权利之一。其次，我国社会救助制度的相关法律法规没有完整出台，社会救助还存在无法可依的问题。虽然目前已出台一些社会救助方面的单独的政策或条例，但是完整、配套的法律法规机制还尚未形成，这就易导致社会救助制度不规范、政策不连贯等问题的出现。</p> \n<p>　　社会救助制度的相关管理机制不完善。当前，我国尚未形成一套较为系统和完整的社会救助标准体系，区域之间、城乡之间的情况都存在差异，且一地政府部门制定政策的水平直接决定当地社会救助标准的合法性和可适性，如果地方政府自行制定社会救助的标准，显然会造成我国各地社会救助标准的参差不齐，在实施过程中也会出现较大的差异，这不利于社会公平发展，也不利于我国新型城镇化建设的推进。</p> \n<p>　　社会救助制度的外部监管机制仍然不到位。虽然目前我国已经出台了社会救助方面的相关监督机制条款，但是在实际过程中并不能对这些监管机制进行有效实施。比如，虽然国家民政部门等联合出台了《关于加强对抗震救灾资金物资监管的通知》和《关于加强对抗震救灾资金物资监督检查工作的意见》，但是在实际抗灾救灾过程中仍然存在实际效果与预期表现差异明显的情况，这也是社会救助机制外部监管不到位的表现。</p> \n<p>　　<strong>我国社会救助制度改革的几大瓶颈约束</strong></p> \n<p>　　城乡二元结构突出，造成社会救助制度二元化。虽然我国城镇化不断推进，但是城乡二元结构依然明显，城市二元户籍制度一直存在。正是由于这种户籍制度，我国城镇居民可以享受国家公共财政提供的基本公共服务，但是农村居民却只能通过自己筹资或者农村集体筹资解决公共服务问题，而国家财政仅给予较少补助。由于国家在基本公共服务制度方面的城乡二元化对待，导致本就存在二元结构的城乡公共服务不但在供给量上落差越来越大，而且在供给主体、供给方式、资金渠道等多方面也不断拉大差距。这种二元的城乡结构，直接导致了社会救助制度二元化，不利于社会救助制度的改革。</p> \n<p>　　社会救助的观念陈旧，政府对社会救助责任落实不到位。在我国城镇化建设不断推进的过程中，经济社会发展取得了明显的成效，而当前国内的贫困问题不仅仅在于经济上的贫困，更在于人力资本的欠缺。我国政府对于新型的贫困化认识存在不足，直接导致政府社会救助观念停滞在经济贫困上，社会救助观念更新步伐滞缓。</p> \n<p>　　国家财政支撑力度不够，长效投入机制欠缺。相关统计显示，2014年发达国家在社会保障的支出占国家总财政的份额大约在33%左右，发展中国家一般在27%左右。在我国，即使加上医疗保障以及保险基金等方面的支出，2014年的社会保障支出占全国总财政的份额仍然只有23%，仍低于发展中国家平均水平。这一问题的主要原因在于我国的社会保障资金的供给渠道单一，主要为财政供给，缺乏长效投入机制。</p> \n<p>　　社会救助渠道存在短板，公共决策制度不健全。当前，我国的社会救助在政策制定的过程中仍然延续了单一的自上而下式的渠道，但是由于社会信息不对称和社会需求多元化等的影响，国内有相当一部分的社会救助对象仍然无法充分享受到自己希望享受到的服务。也就是说，我国目前仍缺乏社会救助服务的组织选择权和决策权，社会救助的兜底作用并没有充分发挥出来。</p> \n<p>　　<strong>推进城乡社会救助一体化，不断发挥社会救助兜底的建议</strong></p> \n<p>　　在新型城镇化的总体道路框架下，消除城乡二元结构成为一大重要任务，城乡一体化建设势在必行。</p> \n<p>　　健全社会救助制度的模式。首先，要进一步健全医疗卫生领域的社会救助制度。医疗卫生服务是我国社会救助的重中之重，国内各地相关政府部门应对医疗卫生制度的改革创新给予充分重视。其次，要建立健全农村的专项社会救助制度。一是要进一步完善农村的“五保”制度，确保农村低收入群体可以充分享受到政府的专项社会救助；二是要强化农村养老公共服务设施的建设与规范，保证农村老年人口“应养尽养”。</p> \n<p>　　规范社会救助资金的筹措与管理。首先，要充分完善社会救助资金筹措机制，确保资金来源渠道多元化。一方面，我国各地政府部门自身要继续强化财政资金对社会救助的投入，要合理配置中央及地方的社会救助资金结构，也要保证资金及时有效落实。另一方面，要积极规范社会救助资金的支付渠道和方式，地方政府要确保对资金准确预算，并规范资金转移支付的相关渠道与程序。此外，还要积极调动民间社会资本，积极参与社会救助事业发展。其次，要规范社会救助资金的运用管理。注重内外结合，政府部门要加强对资金款项核算与运用的监管，同时也要鼓励社会强化监管，保证社会救助资金运用透明化。</p> \n<p>　　健全社会救助制度的相关管理制度。首先，要明确政府职责。中央应出台文件，合理确定各级政府在社会救助方面的职责与权限，促进社会救助管理制度规范化。政府部门要加强相互沟通，有效避免社会救助发生重叠等问题。其次，要强化社会有序参与。政府部门应发挥带头作用，联合民间组织共同参与社会救助，形成合力。最后，加强社会救助服务提供者的整体素质培育，全面促进社会救助制度供给的专业化。</p> \n<p>　　保障社会救助制度有法可依。首先，要继续强化我国社会救助制度的立法工作。中央要对社会救助制度立法进行统筹，各相关部门之间要形成联动立法机制，同时明确责任，各司其职。其次，要加强社会基层的广泛调研。通过社会调研，进一步认识基层社会情况，实时为制定接地气的社会救助法律制度提供思路，也从中修正原有法律制度的缺陷。最后，要严格社会救助制度的立法过程。在社会救助制度的立法过程中，相关部门应严格根据法定程序执行任务，并相应地对立法进行指导，保证社会救助相关法律制定的科学性和合理性。</p> \n<p>　　[参考文献]</p> \n<p>　　[1]国家发展和改革委员会社会发展研究所课题组、谭永生、关博等：《我国社会救助制度的构成、存在问题与改进策略》，《经济纵横》，2016年第6期。</p> \n<p>　　（编辑：朱振民）</p> \n<p>　　来源：《人民论坛》，2016年11月24日</p> \n<p class=\"article-editor\">责任编辑：周夏莹 </p>', '2017-04-05 16:46:12', '作者：乔青水、宋颖洁社会救助制度是关乎我国民生发展大计的重要制度，但在我国城镇化建设过程中，社会救助制度改革的步伐滞后于人民生活水平提升的步伐，尤其是农村地区居民在享受社会救助上存在明显缺口。未来，我国将深入推进新型城镇化建设，因此社会救助', '0', null, null, '1', '0', '社会,制度,我国', '2017-04-05 16:46:12', '1', null, null, '0', null, 'noarchive', '乔青水、宋颖洁：如何发挥社会救助的兜底作用', null, '1');
INSERT INTO `tz_content` VALUES ('116', 'keke', null, null, '<p>　　[环球网综合报道]3月16日，欧盟委员会批准向乌克兰提供6亿欧元（约合44.1亿元人民币）的第二笔财政援助。据俄新社4月4日消息，乌克兰总统彼得•波罗申科4日证实，乌克兰方面已收到该拨款。</p> \n<p>　　波罗申科在其“推特”上发文称：“今天欧盟向乌克兰支付了6亿欧元作为第2笔资金支持，这是为稳定和重建乌克兰经济所给予的宏观援助的一部分。”</p> \n<p>　　俄新社报道称，国际货币基金组织4月3日批准在金融援助项目框架下向乌克兰提供总计10亿美元（约合68.9亿人民币）的新一笔贷款，乌克兰期望于近日获得该款项。</p> \n<p>　　乌克兰连日来频繁得到国际组织的资金援助，但其仍期望获得更多。俄新社报道称，乌克兰国家银行副行长奥列格•丘里3日在新闻发布会上表示，乌克兰国家银行期望于年底前获得国际货币基金组织共计约45亿美元（约合309.9亿人民币）的3笔贷款，以及欧盟委员会大约13亿美元（约合89.5亿人民币）的资金支持。乌方同时希望，2018年乌国家银行能获得国际货币基金组织39亿美元（约合268.6亿人民币）的贷款。他还指出，乌方期望于本年度第2季度末获得国际货币基金组织的下一笔贷款。</p> \n<p>　　对于期望外部援助的行为，乌克兰总理解释称这只是“暂时措施”。俄罗斯国际文传电讯社4月5日报道称，乌克兰总理弗拉基米尔•格罗伊斯曼4日声明称，“我们成功地延续了与国际货币基金组织的合作，外部资金的支持只是暂时的。我们应该学着开发国内资源”。（实习编译：齐莹&nbsp;审稿：仲伟东）</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:12', '[环球网综合报道]3月16日，欧盟委员会批准向乌克兰提供6亿欧元（约合44.1亿元人民币）的第二笔财政援助。据俄新社4月4日消息，乌克兰总统彼得•波罗申科4日证实，乌克兰方面已收到该拨款。波罗申科在其“推特”上发文称：“今天欧盟向乌克兰支付', '0', null, null, '1', '0', '欧盟,国际货币基金组织,资源', '2017-04-05 16:46:12', '1', null, null, '0', null, 'noarchive', '乌克兰获欧盟6亿欧元援助 盼国际组织再援手', null, '1');
INSERT INTO `tz_content` VALUES ('117', 'keke', null, null, '<link rel=\"stylesheet\" href=\"http://ent.sina.com.cn/css/470/20120928/style_videolist.css\" charset=\"GBK\" /> \n<div class=\"artical-player-wrap\" style=\"display:block;\"> \n <div class=\"a-p-hd\"> \n  <div id=\"J_Article_Player\">\n    视频加载中，请稍候... \n  </div> \n  <div class=\"a-p-info\"> \n   <label class=\"fl autoplayctrl\" suda-uatrack=\"key=videoq&amp;value=autoplay\" style=\"display:none;\"> <input type=\"checkbox\" checked=\"\" id=\"J_Video_Autoplay\" /> 自动播放 </label> \n   <span id=\"J_Video_Source\"> </span> \n  </div> \n </div> \n <div class=\"a-p-bd a-p-bd-b\" id=\"J_Play_List_Wrap\" style=\"display:none;\"> \n  <div class=\"a-p-slide\"> \n   <div class=\"a-p-s-list clearfix\" id=\"J_Play_List\"> \n    <div class=\"a-p-s-item J_Play_Item\" play-data=\"250925608-1-\" url-data=\"http://video.sina.com.cn/view/250925608.html\" title-data=\"公司买8吨重大鲸鱼分块宰杀腥\" source-data=\"\"> \n     <a href=\"javascript:;\" class=\"a-p-s-img\" hidefocus=\"true\" title=\"公司买8吨重大鲸鱼分块宰杀腥\"> <img width=\"120\" height=\"90\" src=\"http://p.ivideo.sina.com.cn/video/250/925/608/250925608_400_300.jpg\" alt=\"公司买8吨重大鲸鱼分块宰杀腥\" /> <i class=\"a-p-s-play\">play</i> <span class=\"a-p-s-txt\">公司买8吨重大鲸鱼分块宰杀腥</span> </a> \n    </div> \n   </div> \n   <a href=\"javascript:;\" class=\"a-p-s-prev\" id=\"J_Player_Prev\">向前</a> \n   <a href=\"javascript:;\" class=\"a-p-s-next\" id=\"J_Player_Next\">向后</a> \n  </div> \n </div> \n <script type=\"text/javascript\" charset=\"GBK\" src=\"http://www.sinaimg.cn/ty/sinaui/scrollpic/scrollpic2012070701.min.js\"></script> \n <script type=\"text/javascript\" src=\"http://www.sinaimg.cn/dy/js/jquery/jquery-1.7.2.min.js\"></script> \n <script type=\"text/javascript\" charset=\"GBK\" src=\"http://news.sina.com.cn/js/792/2015-03-13/149/videolist-2.js\"></script> \n <script type=\"text/javascript\">\r\n        /*自动播放1*/\r\n		//var AUTOPLAY = 1;\r\n		var AUTOPLAY = 0; //2015.11.25日通知改成0\r\n        /*连播1*