CONTIPLAY = 1;\r\n        /*处理自动播放选项和cookie*/\r\n        (function() {\r\n            var Tool = CommonTool;\r\n            var chk = Tool.byId(\'J_Video_Autoplay\');\r\n            var ua = navigator.userAgent.toLowerCase();\r\n            var isIOS = /\\((iPhone|iPad|iPod)/i.test(ua);\r\n            if (isIOS) {\r\n                console.log(chk.parentNode.style.display);\r\n				//chk.parentNode.style.display = \'none\'; //2015.11.25 隐藏自动播放选项\r\n                return;\r\n            }\r\n			//chk.parentNode.style.display = \'\'; //2015.11.25 隐藏自动播放选项\r\n            var clickCookie = function() {\r\n                Tool.bindEvent(chk, \'change\',\r\n                function() {\r\n                    var chked = chk.checked;\r\n                    Tool.writeCookie(\'ArtiVAuto\', (chked ? 1 : 0), 24 * 365 * 10, \'/\', \'.sina.com.cn\');\r\n                });\r\n            }\r\n            var byCookie = function() {\r\n                var coo = Tool.readCookie(\'ArtiVAuto\');\r\n                if (coo) {\r\n                    if (parseInt(coo) == 0) {\r\n                        chk.checked = false;\r\n                        AUTOPLAY = 0;\r\n                    }\r\n                }\r\n            };\r\n            clickCookie();\r\n            byCookie();\r\n        })();\r\n        /*获取第一个视频vid*/\r\n        var firstItem = CommonTool.byClass(\'J_Play_Item\', \'J_Play_List\')[0];\r\n        var fInfo = firstItem.getAttribute(\'play-data\').split(\'-\');\r\n        var fVid = fInfo[0];\r\n        var fPid = fInfo[1];\r\n        var sinaBokePlayerConfig_o = {\r\n            container: \"J_Article_Player\", //Div容器的id\r\n            width: 525,\r\n            height: 430,\r\n            playerWidth: 525, //宽\r\n            playerHeight: 430, //高\r\n            autoLoad: 1, //自动加载\r\n            autoPlay: AUTOPLAY, //自动播放\r\n            as: 0, //广告\r\n            pid: fPid,\r\n            tjAD: 0, //显示擎天柱广告\r\n            tj: 0, //片尾推荐\r\n            continuePlayer: 1, //连续播放\r\n            casualPlay: 1, //任意拖动视频\r\n            head: 0, //播放片头动画\r\n            logo: 0, //显示logo\r\n            share: 0,\r\n			thumbUrl: \"\"\r\n        };\r\n    </script> \n <script src=\"http://sjs2.sinajs.cn/video/sinaplayer/js/page/player.js\"></script> \n <script type=\"text/javascript\">\r\n        (function() {\r\n            var toggle = function(id, hide) {\r\n                var e = CommonTool.byId(id);\r\n                var par = e.parentNode;\r\n                if (hide) {\r\n                    CommonTool.addClass(par, e.className + \'_disabled\');\r\n                } else {\r\n                    CommonTool.removeClass(par, e.className + \'_disabled\');\r\n                }\r\n            }\r\n            var scroll = new ScrollPic();\r\n            scroll.scrollContId = \"J_Play_List\"; //内容容器ID\r\n            scroll.arrLeftId = \"J_Player_Prev\"; //左箭头ID\r\n            scroll.arrRightId = \"J_Player_Next\"; //右箭头ID\r\n            scroll.listEvent = \"onclick\"; //切换事件\r\n            scroll.frameWidth = 532; //显示框宽度 **显示框宽度必须是翻页宽度的倍数\r\n            scroll.pageWidth = 133 * 3; //翻页宽度\r\n            scroll.upright = false; //垂直滚动\r\n            scroll.speed = 10; //移动速度(单位毫秒，越小越快)\r\n            scroll.space = 15; //每次移动像素(单位px，越大越快)\r\n            scroll.autoPlay = false; //自动播放\r\n            scroll.autoPlayTime = 5; //自动播放间隔时间(秒)\r\n            scroll.circularly = false;\r\n            scroll._move = scroll.move;\r\n            scroll.move = function(num, quick) {\r\n                scroll._move(num, quick);\r\n                toggle(scroll.arrRightId, scroll.eof);\r\n                toggle(scroll.arrLeftId, scroll.bof);\r\n            };\r\n            scroll.initialize(); //初始化\r\n            toggle(scroll.arrLeftId, scroll.bof);\r\n        })();\r\n    </script> \n <script>\r\n	var autoPlayCheck = $(\"#J_Video_Autoplay\")[0].checked;\r\n		var autoPlayFlv = 0;\r\n		var autoPlayH5 = false;\r\n		if(autoPlayCheck){\r\n			autoPlayFlv = 1;\r\n			var autoPlayH5 = true;\r\n		}\r\n	var flashConfig = {\r\n		url: \"\",	// flash播放器地址,\r\n		container : \"J_Article_Player\",\r\n		id: \"myMovie\",\r\n		width: 525,\r\n		height: 430,\r\n			params : {\r\n			 allowNetworking : \"all\",\r\n			 allowScriptAccess : \"always\",\r\n			 wmode : \"opaque\",\r\n			 allowFullScreen : \"true\",\r\n			 quality : \"high\"\r\n			},\r\n			attributes: {},\r\n			flashvars: {\r\n				//autoPlay: autoPlayFlv,  //是否自动播放\r\n				autoPlay: 0,  //11.25通知改成0\r\n				loop: 0,		\r\n				autoLoad: 1,\r\n				thumbUrl: \'\',\r\n				tj: 0,\r\n				as: 0\r\n			},\r\n			h5attr: {\r\n				//autoPlay: autoPlayH5,  //是否自动播放\r\n				autoPlay: false,  //11.25通知改成false\r\n				controls: true, //是否显示控制条\r\n				loop: false,		\r\n				poster: \'\', //视频加载前欲加载的图片地址，即播放器一开始显示的截图\r\n				preload: \'auto\'\r\n			}\r\n	  };\r\n		var videoList = [\r\n						{\r\n				video_id: 250922205,\r\n				pid:1,\r\n				url: \'http://video.sina.com.cn/view/250922205.html\',\r\n				title:\'省长与市委书记“串供”\'//,\r\n			//swfOutsideUrl:\"http://you.video.sina.com.cn/api/sinawebApi/outplayrefer.php/video_id=250922205/s.swf\"\r\n			},\r\n		]\r\n	</script> \n <script>\r\n	(function($){\r\n	var Play = {\r\n		init: function(flashConfig, videoList){\r\n			this.flashConfig = flashConfig;\r\n			this.videoList = videoList;\r\n			this.playVideo = playVideo;\r\n			this.prev = this.current = 0;\r\n			this.length = this.videoList.length;\r\n			this.contNode = $(\"#J_Video_Autoplay\");\r\n			this.titleNode = $(\"#J_Video_Source\");\r\n			this.playListNode = $(\"#J_Play_List .J_Play_Item\");\r\n			if(videoList.length!==1){\r\n				//显示小图\r\n				CommonTool.byId(\'J_Play_List_Wrap\').style.display=\'\';\r\n			}\r\n			if(videoList.length<5){\r\n				CommonTool.byId(\'J_Player_Prev\').style.display=\'none\';\r\n				CommonTool.byId(\'J_Player_Next\').style.display=\'none\';\r\n			}\r\n			this.initPlayer();\r\n			this.bind();\r\n		},\r\n		bind: function(){\r\n			var _this = this;\r\n			$(\"#J_Play_List\").on(\"click\", \".J_Play_Item a\", function(e){\r\n				e.preventDefault();\r\n				_this.playCurrent($(this));\r\n			});\r\n		},\r\n		initPlayer: function(){\r\n			var _this = this;\r\n			this.player = this.playVideo(this.flashConfig);\r\n			this.player.init(this.videoList[this.prev]);\r\n			this.player.on(\"playCompleted\", function(){\r\n				_this.playNext();\r\n			});\r\n			this.playListNode.eq(0).addClass(\"selected\");\r\n			this.titleNode.html(this.videoList[0].title);\r\n		},\r\n		playCurrent: function($this){\r\n			this.prev = this.current;\r\n			this.current = $this.parents(\".J_Play_Item\").index();\r\n			this.play(this.prev, this.current);\r\n		},\r\n		playNext: function(){\r\n			if(!this.contNode[0].checked){\r\n				return;\r\n			}\r\n			this.prev = this.current;\r\n			if(this.current >= this.length - 1){\r\n				return;\r\n			}\r\n			this.current++;\r\n			this.play(this.prev, this.current);\r\n		},\r\n		play: function(prev, current){\r\n			this.player.playVideo(this.videoList[current]);\r\n			this.titleNode.html(this.videoList[current].title);\r\n			this.playListNode.eq(prev).removeClass(\"selected\");\r\n			this.playListNode.eq(current).addClass(\"selected\");\r\n		}\r\n	}\r\n	Play.init(flashConfig, videoList);\r\n})(jQuery);\r\n</script> \n</div> \n<!--mce-plugin-videoList2[{\"listPlay\":true,\"videos\":[{\"title\":\"\\u7701\\u957f\\u4e0e\\u5e02\\u59d4\\u4e66\\u8bb0\\u201c\\u4e32\\u4f9b\\u201d\",\"videoid\":\"250922205\",\"ad\":true,\"source\":\"\",\"url\":\"http://video.sina.com.cn/view/250922205.html\",\"img\":\"http://p.ivideo.sina.com.cn/video/250/922/205/250922205_400_300.jpg\",\"largeImg\":\"http://p.ivideo.sina.com.cn/video/250/922/205/250922205_400_300.jpg\",\"smallImg\":\"http://p.ivideo.sina.com.cn/video/250/922/205/250922205_400_300.jpg\",\"tags\":\"\"}],\"downloadDescr\":\"\",\"videoCard\":false}]mce-plugin-videoList2--> \n<p>　　[同期声]罗东川（中央纪委案件审理室主任）</p> \n<p>　　实际上权力给它做了一个切分，它就体现了一个权力的监督制衡，不是由一个部门把一个案件能够主宰，整个过程能够进行处理。比如说审理我就对你调查这些事实，包括你的取证，你的定性这些准不准，我还要来给你们把一道关，避免他在操作上，我有一些自己的想法或者有一些不合规定的东西在里面。</p> \n<p>　　[解说]为了加大自我监督力度，近两年还有一项重要举措是开展“一案双查”。在查处一个案件的过程中，既要查党员领导干部的违纪问题，又要查执纪过程中是否有违纪违规行为，一旦发现也要严肃问责。</p> \n<p>　　[解说]2016年1月，四川省委原副书记、原省长魏宏被立案审查，作为一名落马的省部级干部，这一消息已经广为人知。而并不广为人知的是，2016年2月，中央纪委成立调查组，对魏宏和四川省资阳市原市委书记李佳案件开展了“一案双查”。</p> \n<p>　　[同期声]李亚群（中央纪委第十一纪检监察室副局级纪律检查员 监察专员）</p> \n<p>　　我们在审查魏宏的问题的时候，发现魏宏对有关的违纪事实拒不承认，有的避重就轻，同时我们感觉到其中应该是曾经跟李佳进行过有关方面的信息沟通，李佳本人的口供也发生了重大的变化，应该是在这个过程当中，跟魏宏有过这种信息方面的沟通，进行过串供。</p> \n<p>　　[解说]李佳人在看守所看押，怎么能和魏宏进行串供呢？最大的可能性就是有“内鬼”帮忙，“一案双查”由此展开。最终调查发现，检察机关、公安机关有3名领导干部从中帮助串供。此外，还查出四川省纪委调查李佳案的负责人李世成，曾三次和李佳单独见面。</p> \n<p>　　[同期声]李世成（四川省纪委副书记）</p> \n<p>　　我就很错误地认为，不涉及秘密就可以，确实没有意识到，政治敏锐性就是不强。</p> \n<p>　　[解说]李佳由于自身存在问题，听到风声后感到紧张，约李世成见面，想观察和打听自己是否会被调查。按照规定，调查人员如果要和调查对象接触，一是要报经领导批准，二是必须有两人以上，目的就是要严防跑风漏气的可能。不管出于什么原因违背程序和规定，就必须严肃问责。</p> \n<p>　　[同期声]李世成（四川省纪委副书记）</p> \n<p>　　我当时的想法和行为是不对的，甚至是错误的，给我警告处分，我是服从这个处理决定的。我自己现在的工作当中遇到什么问题，我就得在这方面多根弦，就得想一想。</p> \n<p>　　[解说]在对魏宏、李佳案“一案双查”的过程中，还发现省纪委存在违反程序和规定的问题。中央纪委责令四川省纪委以此为契机，对全省执纪工作展开专项检查，对发现的问题进行整改。</p> \n<p>　　[同期声]邓顺贵（四川省纪委副书记）</p> \n<p>　　我们查找出，从线索的处置、初核、立案，到审理，到移送，整个有16个方面的问题。省纪委常委会就决定，今年我们把它作为制度建设年，针对我们存在的问题，建章立制，来规范它。让我们能够更规范地进行纪律审查，这对我们的工作有好处。</p> \n<p class=\"article-editor\">责任编辑：隗俊 </p>', '2017-04-05 16:46:33', '[同期声]罗东川（中央纪委案件审理室主任）实际上权力给它做了一个切分，它就体现了一个权力的监督制衡，不是由一个部门把一个案件能够主宰，整个过程能够进行处理。比如说审理我就对你调查这些事实，包括你的取证，你的定性这些准不准，我还要来给你们把一', '0', null, null, '0', '0', '落马,中纪委,内鬼', '2017-04-05 16:46:33', '1', null, null, '0', null, 'noarchive', '四川原省长魏宏被查 女市委书记狱中与其串供', null, '1');
INSERT INTO `tz_content` VALUES ('208', 'keke', null, null, '<div class=\"img_wrapper\"> \n <img alt=\"高考前，徐玉玉在教室学习。受访者供图。\" src=\"http://n.sinaimg.cn/news/transform/20160825/3pXX-fxvixet3956371.jpg\" /> \n <span class=\"img_descr\">高考前，徐玉玉在教室学习。受访者供图。</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"8月6日，徐玉玉把录取通知书图片上传到QQ空间。\" src=\"http://n.sinaimg.cn/news/transform/20160825/yH1V-fxvecqa7625536.jpg\" /> \n <span class=\"img_descr\">8月6日，徐玉玉把录取通知书图片上传到QQ空间。</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"2016年3月27日，在教室里好友们为徐玉玉过18岁生日。受访者供图。\" src=\"http://n.sinaimg.cn/news/transform/20160825/J8bw-fxvecqa7625550.jpg\" /> \n <span class=\"img_descr\">2016年3月27日，在教室里好友们为徐玉玉过18岁生日。受访者供图。</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"骗子提供给蔡芹（化名）的“通缉令”。\" src=\"http://n.sinaimg.cn/news/transform/20160825/Whuy-fxvixer7182984.jpg\" /> \n <span class=\"img_descr\">骗子提供给蔡芹（化名）的“通缉令”。</span> \n</div> \n<div class=\"img_wrapper\"> \n <img alt=\"徐玉玉和好友在一起。受访者供图。\" src=\"http://n.sinaimg.cn/news/transform/20160825/9qvE-fxvixet3956387.jpg\" /> \n <span class=\"img_descr\">徐玉玉和好友在一起。受访者供图。</span> \n</div> \n<p>　　钱刚汇出，密集的雨点落了下来。徐玉玉开始等待。半小时过去，钱没有回来。她再回拨过去，“电话已经关机”。</p> \n<p>　　从派出所走出来，她迈上了父亲的灰色金鹏电动三轮车。徐连彬发动车，走了三分钟，他想起刚下过雨，怕女儿着凉，“想叮嘱她穿上外套”。</p> \n<p>　　一回头，发现坐在马扎上的<a href=\"http://news.sina.com.cn/s/wh/2016-08-24/doc-ifxvixeq0404119.shtml\" target=\"_blank\">徐玉玉</a>，已经歪倒在车里。徐连彬停车去抱女儿，“身子都软了。”120赶到时，“人都快不行了。”</p> \n<p>　　文|新京报记者李兴丽 张维 王煜 实习生 张笛扬 曹慧茹 龚晨霞</p> \n<p>　　编辑 | 苏晓明</p> \n<p>　　母亲李自云想起女儿就哭，父亲徐连彬忍着悲伤接受一波又一波的媒体采访，“我们孩子太老实了，希望其他家庭不要再受骗。”</p> \n<p>　　8月19日，18岁的临沂罗庄女孩徐玉玉，接到了一通诈骗电话。即将踏入大学的她被骗走上大学的费用9900元。在报警回家的路上，她突然心脏骤停，经医院抢救无效，不幸于21日离世。</p> \n<p>　　剥洋葱（微信ID：boyangcongpeople）调查发现，同一时期，接到类似诈骗电话的学生并非徐玉玉一人。与徐玉玉同一乡镇的校友也险遭诈骗；而在临沂市河东区的一个村庄，一名学生被骗走6800元学费。徐玉玉的多位同学表示，他们和家人都曾接到过推销或诈骗电话。</p> \n<p>　　徐玉玉的去世引发了社会对个人信息泄露和电话诈骗的讨论。全国人大代表、南京邮电大学校长杨震，曾在两会上建议尽早启动个人信息保护法立法。而徐玉玉，今年考上的正是南京邮电大学。</p> \n<p>　　<strong>等待开学的“大学生”</strong></p> \n<p>　　刚满18岁的徐玉玉出生在临沂市罗庄区高都街道中坦社区。姐姐徐林中国海洋大学本科毕业后，几经努力，去了新加坡的化工厂工作。</p> \n<p>　　在中坦社区，徐玉玉是徐家第二个有出息的孩子。她入读的是临沂第十九中学的文科实验班，成绩常年保持在班级前五名，甚至一度冲上全市前20名。</p> \n<p>　　在班主任潘宝建的印象中，徐玉玉是英语课代表，一心想学小语种，“如果正常水平发挥，最少是山东大学。”</p> \n<p>　　今年高考，发挥不算如意的徐玉玉以568分的成绩，被南京邮电大学英语专业录取。8月6日，她在QQ空间晒出录取通知书和学校的手绘地图。“你是我（身边）最争气的同学，加油啊！”同学留言。</p> \n<p>　　“一定会的！”徐玉玉回复。</p> \n<p>　　入校报到的时间定在9月1日。徐连彬从半年前就开始为女儿的学费发愁。妻子李自云腿部残疾，全家开销都靠他一人在村子附近的建筑工地打零工。</p> \n<p>　　在徐玉玉的多位同学看来，勤奋、俭省，是她身上挥之不去的标签。</p> \n<p>　　作为一名住校生，徐玉玉每月的开销只有200元左右。同学蒋奇透露，学校食堂一荤一素，价格通常在5元左右。“徐玉玉常常只点素菜，或者光吃馒头。”</p> \n<p>　　每年过年的压岁钱她也攒了起来。</p> \n<p>　　临近开学，徐连彬凑了半年，8000多的学费“还是没攒够”。</p> \n<p>　　8月17号，他带着女儿到区教育局办理了针对贫困学生的助学金申请。隔天，接到教育局电话，“说钱过几天就能发下来。”</p> \n<p>　　按照南京邮电大学录取通知书中的要求，学生需要在20号之前把学费打进学校寄送的银行卡里，学校会自动扣款。</p> \n<p>　　8月18号，徐连彬借了亲戚一部分钱，凑足了1万块带着女儿去银行存了：“学费8000多，剩下的是生活费，不到2000。”怕女儿去了南京不够花，徐连彬又宽慰她，“等爸爸发了工资，再给你打点生活费。”</p> \n<p>　　19号下午4点半，骗子的电话直接打到了徐玉玉母亲李自云的手机上。听说是要“发放什么助学金”，她喊了女儿来接电话。</p> \n<p>　　入秋后的临沂，午后天边挂着乌云。徐玉玉说，快下雨了明天再去领。</p> \n<p>　　“19日是发放助学金的最后一天，晚了就拿不到了。”对方说，20分钟内赶到ATM机旁，通过ATM机就能领到2600元助学金。</p> \n<p>　　李自云向剥洋葱（微信ID：boyangcongpeople）回忆，为了拿到2600元助学金，给爸爸减轻负担，徐玉玉没有犹疑，“抓起家里的雨披，骑着车就去了银行。”</p> \n<p>　　<strong>被欺诈电话包围</strong></p> \n<p>　　离家3里之外就是建设银行的ATM机。按照电话里的要求，徐玉玉插了3次卡，没有取到助学金。</p> \n<p>　　乌云在天边聚集，天黑了下来。因为此前一天接到的教育局电话，徐玉玉并没有怀疑。按照电话里的指示，她又换到了附近的农业银行。</p> \n<p>　　对方问她身上是否有其他银行卡。徐玉玉提到了刚刚存入1万元学费的银行卡。</p> \n<p>　　“那张交学费的银行卡还未激活，”对方要求她通过ATM机取出9900元，把钱汇入指定的账号，以“激活银行卡”，并声称半小时内，会把9900元连同2600元助学金一起汇回来，徐玉玉没有怀疑就转出了9900元。</p> \n<p>　　钱刚汇出，密集的雨点落了下来。徐玉玉开始等待。半小时过去，钱没有回来。她再回拨过去，“电话已经关机”。</p> \n<p>　　冒着雨，徐玉玉骑着自行车飞奔回家。“妈，我可能被骗了学费。”徐玉玉跟妈妈边分析边哭。李自云看女儿哭得厉害，劝解她，“全当花钱买了个教训。”</p> \n<p>　　徐玉玉接受不了本就贫困的家庭横遭骗局，她拉着父亲去了派出所。</p> \n<p>　　徐玉玉不知道，几乎同时，家住临沂市河东区汤头镇的蔡芹也被骗走了6800元学费。</p> \n<p>　　“没有人会觉得我可怜，一切都是我自找的，怪就怪自己笨，因为自己的笨，伤害了一个家庭。”8月19日晚，从派出所回到家的蔡芹在微博上发泄心里的自责。</p> \n<p>　　一名自称“上海市嘉定分局刑侦队”的警官称，蔡芹涉及一起非法洗钱案，正在被通缉，“如果没证据证明合法的资金流动，要在监狱里度过后半生。”</p> \n<p>　　同样，对方称，30分钟彻查结束后会把钱退回。被吓慌的蔡芹通过支付宝汇出了银行卡里的6800元学费。</p> \n<p>　　多方信源显示，接到诈骗电话的，并非个例。</p> \n<p>　　“徐玉玉接到诈骗电话的那几天，也有其他班级的学生的爸爸接到类似电话。”潘宝建对剥洋葱（微信ID：boyangcongpeople）说，对方自称是临沂市财政局，通知他们领取2680块的补助。</p> \n<p>　　幸运的是，这位同学的爸爸警惕性很高，让对方提供财政局的固定电话。对方没给。他又联系了班主任，发现“根本没有这回事。”</p> \n<p>　　幸运没有降临在徐玉玉身上。</p> \n<p>　　从派出所走出来，她迈上了父亲的灰色金鹏电动三轮车。徐连彬发动车，走了三分钟，他想起刚下过雨，怕女儿着凉，“想叮嘱她穿上外套”。</p> \n<p>　　一回头，发现坐在马扎上的徐玉玉，已经歪倒在车里。徐连彬停车去抱女儿，“身子都软了。”120赶到时，“人都快不行了。”</p> \n<p>　　<strong>我们的信息是怎么泄露的？</strong></p> \n<p>　　19日晚，在临沂市矿务局医院，徐连彬被告知，女儿“已经开始脑死亡”。</p> \n<p>　　经过抢救，徐玉玉心跳恢复，“但心脏已经供不上血，依靠呼吸机维持生命。”赶来看望徐玉玉的同学郭秋彤隔着重症监护室的玻璃，看到昔日的好友因为呼吸机压迫，“脸都变形了，脸色很难看”。</p> \n<p>　　在班级QQ群里，跟在医院的学生随时报告徐玉玉的状况。学校也紧急给全校学生发了短信，“警惕类似的骗局”。</p> \n<p>　　学生的不安并没有减少。“我们的信息是怎么泄露的？”、“为什么她需要助学金，骗子就找来了”、“很多推销电话，知道的（信息）还不少”……</p> \n<p>　　郭秋彤和同学仔细回忆了可能泄露信息的节点。“高考报名、不作弊保证书、十条警戒线，都填了家长的手机号。”日常学习中，老师为了方便管理学生，还通过飞信，加了部分学生家长。</p> \n<p>　　8月21日晚，同学们的疑惑未及解答，QQ群里传来语音：“告诉大家一个不好的消息，玉玉已经心跳停止了。”</p> \n<p>　　此后，“女孩被骗光学费离世”的消息经转发，引发逾千万阅读，与之相关的电信诈骗也再次被关注。</p> \n<p>　　独立电信分析师付亮向剥洋葱（微信ID：boyangcongpeople）介绍，随着沟通的工具越来越方便，电信诈骗的方式越来越多，“防不胜防”。以经济活动的浙江省为例，公开报道显示，2015年一年，浙江警方受理通讯网络诈骗案件10.71万多起，损失高达15.43亿元，同比分别上升了41.3%和32.8%。</p> \n<p>　　一位不愿具名的基层民警表示，徐玉玉被骗的170和171是虚拟号段，“不用身份证就可以办到”，通过手机号码追踪破案的可能很小。</p> \n<p>　　剥洋葱（微信ID：boyangcongpeople）注意到，工业和信息化部下发的《关于贯彻落实&lt;反恐怖主义法&gt;等法律规定进一步做好电话用户真实身份信息登记工作的通知》，要求电信企业从严落实对入网用户实名登记工作。</p> \n<p>　　与此同时，工信部网安局的一份调查结果显示，目前仍有不少虚拟运营商为了开拓市场，在贯彻执行政策实名制时存在违规行为，这一定程度上为骚扰信息、垃圾信息、诈骗信息泛滥提供了土壤。</p> \n<p>　　“它涉及警方、电信运营商和银行系统”，通信行业门户网站飞象网总裁项立刚称，目前电信诈骗破案难，除了涉及公安、通信、银行等多部门，“诈骗犯效率高，成本低，而警方的效率偏低，没有形成超出区域管理的平台，我认为是大问题。”</p> \n<p>　　项立刚举例称，按照警方属地管理的原则，山东临沂警方想要破案的话，“去广东、广西，花费的成本早就超过被骗的9000多元了。”</p> \n<p>　　<strong>警告</strong></p> \n<p>　　徐玉玉去世后，徐连彬一家从她的衣物中找到了她攒的压岁钱，“有一千多块。”</p> \n<p>　　按照计划，8月31日，徐连彬和妻子带着徐玉玉将从临沂出发到南京。在那里，他们将与从新加坡赶来的大女儿徐林汇合，送徐玉玉迈入大学。</p> \n<p>　　5月底，姐姐徐林回了趟老家。趁着考前放假，她带着妹妹去了附近的家具城，花1500块钱买了一套衣柜和书桌，“想让她高考完，把衣物收拾出来，开学时可以直接带走。”</p> \n<p>　　8月22日，徐玉玉收拾好的行囊被带进了火葬场。原来两姐妹挤着睡觉的屋子“完全变了模样”。</p> \n<p>　　“20日我局立案侦查此案，目前，省厅和市局派出专人指挥、督导该案，刑警大队也参与其中。”8月24日，临沂市公安局罗庄分局相关负责人向新京报透露，此前也处理过电信诈骗案件，但像这次的情况还是第一次遇到，“非常令人痛恨，我们一定会尽全力破案。”</p> \n<p>　　剥洋葱（微信ID：boyangcongpeople）梳理发现，根据今年2月国务院打击治理电信网络新型违法犯罪工作部际联席会议的部署，公安部刑侦局依托“电信诈骗案件侦办平台”，对全国电信诈骗涉案账户实行快速接警止付。</p> \n<p>　　设在公安部刑侦局的全国打击治理电信网络新型违法犯罪专项行动办公室实时审核各地接警录入侦办平台的涉案账户信息，并与相关银行紧密协作，开展紧急止付工作。</p> \n<p>　　公安、通信运营商、银行系统的联合成为各地成立反诈骗中心的一致做法。电信诈骗频发的温州市，请来了包括工农建等银行的5位资金分析专家，电信、移动、联通等通信运营商的3位通信分析专家，以及百度、阿里的5位大数据分析专家。</p> \n<p>　　“中心成立后的一个月，成功截留了475万余元。”浙江公安官网显示，温州同期总报警金额为1400万元，这等于挽回了超过三分之一的损失。</p> \n<p>　　8月24日上午，另一名被骗学生蔡芹接到了派出所所长打来的两通电话。“前一通说案子很受重视。后一通说，我可以申请贫困补助。”但是被骗的钱是否能追回，没有答复。</p> \n<p>　　同日，教育部发布提醒称，广大学生尤其是大学新生，无论是哪个单位或者个人提供资助，不应要求学生到ATM机或网上进行双向互动操作。</p> \n<p>　　临沂公安官方微博也公布了具有针对性的提醒：“当前，各大院校陆续开学，诈骗分子蠢蠢欲动。”提醒称，作案分子在电话里通知受害人按照规定可以向其发放助学金，再以需先存钱激活银行卡等为由诱骗当事人通过银行转账汇款，从而实施诈骗。</p> \n<p>　　（文中徐林和蔡芹为化名）</p> \n<p class=\"article-editor\">责任编辑：瞿崑 SN117</p>', '2017-04-05 16:46:33', '钱刚汇出，密集的雨点落了下来。徐玉玉开始等待。半小时过去，钱没有回来。她再回拨过去，“电话已经关机”。从派出所走出来，她迈上了父亲的灰色金鹏电动三轮车。徐连彬发动车，走了三分钟，他想起刚下过雨，怕女儿着凉，“想叮嘱她穿上外套”。一回头，发现', '0', null, null, '0', '0', '电信诈骗', '2017-04-05 16:46:33', '1', null, null, '0', null, 'noarchive', '女生被骗学费离世 父亲:她倒三轮车里身子软了', null, '1');
INSERT INTO `tz_content` VALUES ('209', 'keke', null, null, '<div class=\"img_wrapper\">\n <img title=\"\" src=\"http://n.sinaimg.cn/news/crawl/20170405/WYN0-fycxmks5707215.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　4月3日下午2时30分左右，“丽水某某车友群”微信群里出现一段侮辱交警的录音，高速交警丽水支队二大队在获取第一手资料并固定好证据之后，跟云和县公安局城南派出所报了案。</p> \n<p>　　发录音的毛某接到派出所的调查电话后赶到了派出所。</p> \n<p>　　民警给他展示了他在微信群里的发言内容，并跟他说明了相关的法律法规。</p> \n<p>　　毛某低头认错，并讲述了事情的原委，4月3日自己驾车带朋友从景宁到云和玩，在高速上没系安全带。下午2点多，被二大队在云和收费站查到，被处罚了200元并记2分。</p> \n<p>　　毛某感觉，在女性朋友面前很没面子，于是就把怨气发到微信群里。</p> \n<p>　　毛某说，过了一段时间自己冷静下来后就把侮辱交警的发言在自己的微信端删除了。</p> \n<p>　　毛某发言传播范围有限，情节轻微并认识到自己的错误，态度较好，民警让毛某先给二大队民警道歉，又让他写了检讨书，并要求第一时间发在群里。</p> \n<p>　　民警提醒，在受到行政处罚时候，你认为你的合法权益受到了侵犯，一定要通过合法的途径解决，法律规定受到行政处罚的可以在60天内向处罚机关的上一级行政机关要求行政复议或者六个月内向处罚单位所在地的人民法院提起行政诉讼。千万不要逞一时之快做糊涂事，到头来你的言行可能会触及治安管理处罚法，严重甚至的可能触及刑法。（完）</p> \n<p>　　来源：钱江晚报</p> \n<p class=\"article-editor\">责任编辑：张迪 </p>', '2017-04-05 16:46:34', '4月3日下午2时30分左右，“丽水某某车友群”微信群里出现一段侮辱交警的录音，高速交警丽水支队二大队在获取第一手资料并固定好证据之后，跟云和县公安局城南派出所报了案。发录音的毛某接到派出所的调查电话后赶到了派出所。民警给他展示了他在微信群里', '0', null, null, '0', '0', '交警,安全带,万象', '2017-04-05 16:46:34', '1', null, null, '0', null, 'noarchive', '小伙开车没系安全带被罚 网上侮辱交警又被罚', null, '1');
INSERT INTO `tz_content` VALUES ('210', 'keke', null, null, '<p>　　中新网4月5日电 据“中央社”报道，近日，美国一名男子在丹佛一甜甜圈店食物挑战时，因试图在80秒内吞下一个约227克的糖霜甜甜圈时不幸噎死。数天前也有人在类似比赛噎到，而在同天丧命。</p> \n<p>　　据报道，当地一名验尸官表示，42岁的马洛夫（Travis Malouf）2日因呼吸道阻塞窒息而死。目击者艾德斯坦（Julia Edelstein）4日说，马洛夫生前参加比赛，得吃下1个“大小如小蛋糕”的甜甜圈。</p> \n<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/translate/20170405/0nxS-fycxmks5698125.jpg\" alt=\"资料图：咖啡与甜甜圈\" data-mcesrc=\"http://n.sinaimg.cn/translate/20170405/0nxS-fycxmks5698125.jpg\" data-mcestyle=\"max-width: 500px;\" data-mceselected=\"1\" data-link=\"\" />\n <span class=\"img_descr\">资料图：咖啡与甜甜圈</span>\n</div> \n<p>　　根据丹佛电视台KUSA-TV接获的声明，比赛优胜者能免费获得甜甜圈，以及证明挑战成功的小徽章，但主办比赛的“巫毒甜甜圈店”（Voodoo Doughnuts）目前已中止这场比赛。</p> \n<p>　　艾德斯坦说，马洛夫将甜甜圈撕成一半后，开始边走边吃，看来是决心要取胜。店员宣布比赛已过30秒后，马洛夫喝了一点水并咬下一口甜甜圈，之后却突然在柜台旁弯腰驼背，开始捶击柜台。</p> \n<p>　　马洛夫脸色铁青倒下时，手中还握着吃剩的甜甜圈，有2名顾客在他倒地前扶住他，也在医护人员赶抵现场前拚命地试着救他。不过，根据验尸官与警方，马洛夫当场便已回天乏术。</p> \n<p>　　对此，巫毒甜甜圈店4日发表声明说：“在这非常艰难的时刻，我们的心与马洛夫家人同在”，“我们已停止所有分店的食物竞赛。”</p> \n<p>　　据了解，20岁的尼尔森（Caitlin Nelson）因数天前参加康乃狄克州费菲尔（Fairfield）圣心大学（Sacred Heart University）的吃松饼比赛时噎到，同样于2日丧命。</p> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:34', '中新网4月5日电据“中央社”报道，近日，美国一名男子在丹佛一甜甜圈店食物挑战时，因试图在80秒内吞下一个约227克的糖霜甜甜圈时不幸噎死。数天前也有人在类似比赛噎到，而在同天丧命。据报道，当地一名验尸官表示，42岁的马洛夫（TravisMa', '0', null, null, '0', '0', '甜甜圈,比赛,大胃王', '2017-04-05 16:46:34', '1', null, null, '0', null, 'noarchive', '美“大胃王”比赛接连出事 男子吞甜甜圈被噎死', null, '1');
INSERT INTO `tz_content` VALUES ('211', 'keke', null, null, '<p>　　[环球网综合报道]特朗普上任后，台湾在“台美关系”上作了不少“试探”。据台《自由时报》5日披露，其中就包括正式提出将台“驻美国台北经济文化代表处”中台北二字改名为“台湾”。　　</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/axmG-fycwyns4846649.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　这项“试探”结果自然是碰壁了。报道称，台“驻美代表处”日前前往美国国务院叩门，不过得到美国官僚体系（事务官阶层）的回应却是：这是在破坏现状。换言之，直接给了不小的“钉子”碰。</p> \n<p>　　消息传回岛内后，台“外交部长”李大维勃然大怒，特地把AIT（美国在台协会）的官员找到办公室，申明所谓“破坏现状”的说法，他不能接受，当面加以抗议。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/FldM-fycxmks5680464.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　报道称，台外交部还查证到“旧怨”，1979年，“驻美馆处”从“驻美大使馆”，改称为“北美事务协调委员会驻美国办事处”，以及1994年再改称为现在的“台北经文代表处”，两次都是美方迳行告知，根本没有讨价还价的余地。</p> \n<p>　　其实，在被美国斥为“破坏现状”前，台湾在已经在日本碰过一个“别致”的墙壁了。与美国一样，日本政府也被询问是否可将“台北驻日经济文化代表处”改名为台湾？</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/t6Pv-fycwyph2034208.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　当然，日本人给出了一个“很是干脆”的答复，东京表示，“你们先搞定华盛顿再说”，报道说，日本的遣词造句虽然比美国要软些，但基本还是个“钉子”。</p> \n<p>　　其实，台湾四处“求改名”、“求突破”与日本人脱不开干系。　去年底，“日本交流协会”宣布，自17年起正式更名为公益财团法人“日本台湾交流协会”，这在当时被岛内绿营视为“大突破”。</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/zxAp-fycwymx3826932.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　在今年3月6日台湾的“立院备询”中，台“外交部长”李大维也公开表示，作为对日本的回应，“亚协”也将改名为“台湾日本关系协会”，全案已经呈报“行政院”，等待核定中。</p> \n<p>　　当时，李大维就已经被问过，台湾“驻美”、“驻日”的“办事处”是否也能改名？当时李大维回应说，“驻外馆处”改名需要双方同意，还在洽谈中。</p> \n<p>　　对于此次在美国的“碰壁”，台媒解释称，美国国务院目前除国务卿之外，其他几个关键位子的人事，到现在还悬缺未完全补齐。因此导致下情无法上达，事务官阶层直接就否决了台湾的要求。</p> \n<p>　　台媒称，台湾改名要求这是触碰出了特朗普新政府成立至今很大的问题。有“外交官员”表示，“这个情况，恐怕还要等到今年夏天之后，才能看看是不是能逐步明朗化。”</p> \n<p>　　对此，有网友讽刺表示，“把台北市先改为台湾市，这样就没有台北这个名词了。”</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/CqcS-fycwymx3826934.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p>　　还有专业知识丰富的网友表示，“台驻美代表处直接找美国国务院就是破坏现状了，美国无论民间组织、政治团体、五百大企业、华尔街、公益或利益团体做政治游说通常是从美国国会开始寻找游说管道，这种新闻突显出台湾驻外人员的训练和常识几乎是零，完全不及格。”</p> \n<div class=\"img_wrapper\">\n <img style=\"max-width: 500px;\" src=\"http://n.sinaimg.cn/translate/20170405/4u8s-fycwyns4846652.jpg\" alt=\"\" />\n <span class=\"img_descr\"></span>\n</div> \n<p class=\"article-editor\">责任编辑：刘光博 </p>', '2017-04-05 16:46:34', '[环球网综合报道]特朗普上任后，台湾在“台美关系”上作了不少“试探”。据台《自由时报》5日披露，其中就包括正式提出将台“驻美国台北经济文化代表处”中台北二字改名为“台湾”。这项“试探”结果自然是碰壁了。报道称，台“驻美代表处”日前前往美国国', '0', null, null, '0', '0', '特朗普,台湾,官员', '2017-04-05 16:46:34', '1', null, null, '0', null, 'noarchive', '台湾在美“代表处”寻求改名碰壁 高官勃然大怒', null, '1');
INSERT INTO `tz_content` VALUES ('212', 'keke', null, null, '<div class=\"img_wrapper\">\n <img src=\"http://n.sinaimg.cn/news/crawl/20170404/BcQG-fycwymx3671026.jpg\" alt=\"\" />\n</div> \n<p>　　【环球网报道 记者 余鹏飞】据韩国“亚洲经济”网站4月4日报道，内忧外患的乐天百货商店3月30日起开展史上最大规模“挥泪大甩卖”，然而在促销开始后的4天销售额反而下降了1.5%。</p> \n<p>　　报道称，韩国经济长期不景气，内需低迷。尽管百货商店正在不断出招刺激消费，但销售情况不见起色，令业界十分担忧。</p> \n<p>　　据相关业界4日消息，乐天百货商店上月销售额较去年同期增长了0.5%。和今年前2个月的负增长(-2%)相比，情况稍有回暖。但学生开学、季节更替等重要消费因素不可忽视，想要突破“消费悬崖”的阻碍仍是任重道远。而其2016年3月的销售额较上年同期(2015年3月)增长达2.6%，也从侧面显示出进入今年以来，消费持续不振。</p> \n<p>　　韩国KBS电视台3月20日曾报道称，受到“萨德”影响，乐天主动关闭中国境内约20家门店，此前有67家乐天在华商场因消防整顿而被勒令停业。乐天集团将为此付出每月1160亿韩元的损失，而停止营业期间还必须继续支付雇员工资。</p> \n<p>　　据悉，乐天集团在华事业占集团总体的比重较大，在韩国的乐天免税店，整体购买额中70%来自中国消费者，远远高于日本以及韩国本地游客的消费额。乐天自1994年进入中国市场以来，覆盖中国24个省市。2016年乐天集团在华营业额高达3.2万亿韩元(约合192.3亿人民币元)。</p> \n<p>　　面临内忧外患的乐天百货商店3月30日起开展史上最大规模的打折促销活动，共有750余个品牌参加，然而在促销开始后的4天(3月30日至4月2日)，销售额反而下降了1.5%。</p> \n<p class=\"article-editor\">责任编辑：陈琰 SN225</p>', '2017-04-05 16:46:34', '【环球网报道记者余鹏飞】据韩国“亚洲经济”网站4月4日报道，内忧外患的乐天百货商店3月30日起开展史上最大规模“挥泪大甩卖”，然而在促销开始后的4天销售额反而下降了1.5%。报道称，韩国经济长期不景气，内需低迷。尽管百货商店正在不断出招刺激', '0', null, null, '0', '0', '乐天,甩卖,萨德', '2017-04-05 16:46:34', '1', null, null, '0', null, 'noarchive', '韩媒:乐天史上最大甩卖 打折4天销售额反降1.5%', null, '1');
INSERT INTO `tz_content` VALUES ('213', 'keke', null, null, '<p>　　原标题：继父性侵继女一审获刑十四年 检察官抗诉二审改判无期徒刑</p> \n<p>　　正在上班的母亲突然接到女儿的电话，女儿反常的举动让母亲有种不祥的预感。经过反复追问，得到的答案如同晴天霹雳：不满14岁的女儿怀孕了。女儿是如何怀孕的？肚子里的孩子又是谁的？且看《法制日报》记者一一道来。</p> \n<p>　　<strong>□法制网记者　徐鹏</strong></p> \n<p>　　11岁，本应是无忧无虑、在父母身旁快乐成长的花季，对于筱泉（化名）来说，这个年龄却成了痛苦的开端……</p> \n<p>　　<strong>晴天霹雳</strong></p> \n<p>　　2015年3月15日，正在山东省青岛市某公司上班的张霞接到了女儿筱泉的电话。这天是周日，本该在家写作业的女儿一反常态，不停地哭着。一种不祥的预感涌上心头，经过反复追问，张霞得到的答案简直如同晴天霹雳：不满14岁的女儿怀孕了。</p> \n<p>　　回到家，张霞不忍细问，带着女儿匆匆赶到医院。当看到女儿的B超检验结果，她更加不敢相信自己的眼睛——胎儿已经31周了。</p> \n<p>　　在张霞的鼓励下，筱泉鼓起勇气，说出了那个名字。他是张霞的同居男友、筱泉的继父，刘庆才。直到现在，张霞才知道，这场罪恶的性侵，已经持续了长达3年之久。</p> \n<p>　　事情还要从6年前说起。那时，张霞刚和丈夫离婚，带着10岁的女儿筱泉一起生活，母女俩相依为命，日子过得倒也平静。不久，经人介绍张霞认识了刘庆才，两人很快就同居了。</p> \n<p>　　2010年，刘庆才和张霞的第一个女儿出生，2011年，他们的儿子出生了。因为没有结婚，两个孩子都无法落户。为此，张霞多次和刘庆才提过领证的事，但是刘一直推脱，每次都说再等等。</p> \n<p>　　“两个人结识后，没有领取结婚证，被告人还打过她的父母，并且骗张霞说：人家给我算命，说我命中无子，你给我生了儿子，我一定会把所有的财产都留给你和儿子。”青岛市人民检察院公诉一处检察官隋蔚说。</p> \n<p>　　被花言巧语迷惑的张霞再也没有提过结婚的事情，她觉得一家人生活在一起热热闹闹的也就知足了。然而，罪恶在悄悄酝酿。</p> \n<p>　　<strong>三年罪恶</strong></p> \n<p>　　2012年，刚满11岁的筱泉读小学五年级了，渐渐有了豆蔻少女的神采。看着像花蕾般绽放的继女，刘庆才心里邪念顿生。3月的一天晚上，张霞带着两个孩子回了娘家，只留下筱泉在自己卧室里写作业，突然，刘庆才推门进来了。</p> \n<p>　　据筱泉后来的陈述，那令她一生都难以抹去的屈辱一幕，就是在这天夜里发生的。未满11岁的少女无论如何也无法与成年男子抗衡。她不敢相信，眼前这个狞笑的恶魔就是平日里道貌岸然、嘘寒问暖的父亲……</p> \n<p>　　筱泉想到了报警。但是，刘庆才恐吓她说：要是敢说出去，就把你和你妈全都撵出去。第二天早晨，刘庆才给了筱泉800元，让她自己买点东西。筱泉也想到过打电话告诉妈妈，可最终还是犹豫了。</p> \n<p>　　“小孩可能对这种事情还不是很清楚，也担心自己说出来会影响母亲的婚姻，因此就没有告诉母亲。”隋蔚说。</p> \n<p>　　一个星期后，趁张霞不在家，刘庆才不顾筱泉正在生理期，再一次性侵了她。</p> \n<p>　　“张霞和被告人都是搞工程的，整天忙于工作，平时的关注点也多是在她和被告人所生的儿女身上。被告人经常利用工程把张霞支出去，当这个小女孩自己单独在家的时候，被告人就趁机对她进行性侵。”隋蔚说。</p> \n<p>　　在随后的3年里，刘庆才几乎每周都会性侵筱泉，而筱泉却在恐惧与羞耻中选择了沉默。</p> \n<p>　　直到2014年12月，筱泉感觉到肚子里有东西踢她，联想到自己已经很久没有来例假，她突然陷入了巨大的恐惧之中。走投无路的筱泉只好求助于刘庆才。而刘庆才却一拖再拖，甚至避而不见。直到2015年3月15日，筱泉突然接到刘庆才的电话，让她打车去一家医院，并给了7000元让她自己去做流产。</p> \n<p>　　<strong>一审宣判</strong></p> \n<p>　　再三犹豫后，筱泉回家了，她给妈妈打了电话。得知真相的张霞泣不成声，她马上带着筱泉去报警。</p> \n<p>　　随着案件的侦查，刘庆才的真实情况浮出水面。也是直到这时，张霞才得知这个与她同床共枕6年之久的人的真实面目。原来，刘庆才不仅结过婚，而且根本没有与妻子离婚。这也正是他一直不同意与张霞领结婚证的真正原因。</p> \n<p>　　“被告人是结过婚的。因为经常在外面干工程，所以他和妻子不是长期在一起。他和被害人的母亲也不是天天在一起，因此两边都不知道对方的存在。”隋蔚说。</p> \n<p>　　暴怒的张霞恨不能扒了刘庆才的皮。她甚至觉得，这辈子，刘庆才已经没脸再见到她们娘俩。然而，让她和筱泉根本想不到的是，刘庆才居然倒打一耙，根本不承认有这回事。</p> \n<p>　　“我没有对她做过任何事情，谁知道她肚子里的孩子是谁的？一定是她这么小的年纪不学好，在外面乱交男朋友。本身就是个败类。”刘庆才说。在接受讯问的时候，刘庆才不仅始终不承认筱泉腹中的胎儿是他的，甚至污蔑母女俩是想趁机敲诈他的钱财。</p> \n<p>　　在涉及未成年人的性侵案中，最难的环节往往是取证。由于孩子还小、大部分受害儿童根本不知道留取证据，而且犯罪场所隐蔽、几乎没有第三方，犯罪嫌疑人又往往拒不承认，想要获得有效证据难乎其难。</p> \n<p>　　在本案中，刘庆才不仅不承认性侵筱泉，还毁灭了一切可能的证据。这让案件的突破陷入僵局。那么，如何才能还这对母女一个公道呢？</p> \n<p>　　“报案后给这个小孩做了引产手术，经过鉴定，胎儿的DNA和小女孩以及被告人的DNA有关联，小女孩和被告人是这个胎儿的生物学父母。”隋蔚说。</p> \n<p>　　然而，一审判决意见却完全出乎筱泉母女的意料：刘庆才犯强奸罪，被判处有期徒刑14年，犯重婚罪判处有期徒刑1年，决定执行有期徒刑14年，剥夺政治权利5年。</p> \n<p>　　检察官认为，一审判决不当，遂提起抗诉。很快，山东省人民检察院决定支持青岛市检察院提起的抗诉。</p> \n<p>　　<strong>二审改判</strong></p> \n<p>　　由于刘庆才拒不认罪、十分嚣张，因此关于本案中证据的认定是摆在检察官面前的一道难题。</p> \n<p>　　“特别是对于本案最重要的证据，对胎儿组织的鉴定意见不认可。但是根据我们审查作出鉴定意见的鉴定机构、鉴定人、鉴定程序，都是合法有效的，这就确保了本案的事实清楚、证据确实充分。”山东省检察院未检处检察官韩绍广说</p> \n<p>　　在二审法庭上，山东省检察院检察官提出了“量刑畸轻，依法应予严惩”的抗诉意见。检察官与刘庆才及其辩护人、专家证人围绕鉴定意见的法律效力和有关焦点问题进行了针锋相对的辩论。最终，二审法院认定鉴定意见合法有效。也就是说，刘庆才的确是筱泉腹中胎儿的生物学父亲，其性侵筱泉是无可辩驳的事实。</p> \n<p>　　“虽然刘庆才和张霞并没有结婚，但实际上他扮演了父亲的角色。在开始的时候女孩对刘庆才十分依赖和尊重，但他却利用这种便利条件犯下了滔天罪责。他的这种行为不仅是对法律的触犯，也是对人伦底线的触碰。”韩绍广说。</p> \n<p>　　法庭上，面对检察官的义正辞严，一直叫嚣着“要你们好看”的刘庆才再也没有了第一次开庭时的嚣张气焰。特别是当检察官提到了为人父却违背人伦底线的质问时，他第一次低下了头。</p> \n<p>　　检察官提出了刘庆才应适用无期徒刑的抗诉意见：“对于性侵未成年人这类案件，我们国家一直采取严厉打击的刑事政策。在2013年，‘两高’‘两部’出台意见严惩性侵未成年人犯罪，在意见中详细系统地规定了对性侵未成年人的犯罪行为予以打击。其实，国外对于性侵未成年人的惩罚也是十分严厉的。比如在德国，凡是与14周岁以下未成年人发生性关系，一律视为强奸，且量刑一般在10年以上，或者被化学阉割。俄罗斯相关法律也有类似规定，1996年美国就颁布了防范未成年人遭遇性侵害的梅根法案，要求公开罪犯身份。以上国家的立法都很严格，其中有些做法对我们有借鉴意义。”</p> \n<p>　　2016年12月，法院作出终审判决，刘庆才犯强奸罪，判处无期徒刑，剥夺政治权利终身；犯重婚罪，判处有期徒刑1年，决定执行无期徒刑，剥夺政治权利终身。</p> \n<p>　　<strong>■　故事背后</strong></p> \n<p>　　山东省人民检察院未检处处长栾驭说，近年来，性侵未成年人案件呈上升趋势，从山东省检察机关的报备情况来看，2016年要比2015年多，并且近几年都有恶性案件发生。如何防范这种犯罪？栾驭认为，第一是要严厉打击此类犯罪。对这种犯罪采取零容忍的态度，从重从严从快打击。通过对这些个案进行监督，使法律得以严格执行。第二是要加强国家和社会对正能量的引导，要让大家树立正确的人生观和世界观。第三要加强对未成年人的法制教育和法制宣传，让未成年人有自我保护意识，要让他们懂法、知法、守法，用法律来维护自己的权益。</p> \n<p>　　来源：法制日报</p> \n<p class=\"article-editor\">责任编辑：柳龙龙 </p>', '2017-04-05 16:46:35', '原标题：继父性侵继女一审获刑十四年检察官抗诉二审改判无期徒刑正在上班的母亲突然接到女儿的电话，女儿反常的举动让母亲有种不祥的预感。经过反复追问，得到的答案如同晴天霹雳：不满14岁的女儿怀孕了。女儿是如何怀孕的？肚子里的孩子又是谁的？且看《法', '0', null, null, '0', '0', '继女,性侵,无期徒刑', '2017-04-05 16:46:35', '1', null, null, '0', null, 'noarchive', '男子性侵未成年继女获刑14年 检方抗诉改判无期', null, '1');
INSERT INTO `tz_content` VALUES ('214', 'keke', null, null, '<link rel=\"stylesheet\" href=\"http://ent.sina.com.cn/css/470/20120928/style_videolist.css\" charset=\"GBK\" /> \n<div class=\"artical-player-wrap\" style=\"display:block;\"> \n <div class=\"a-p-hd\"> \n  <div id=\"J_Article_Player\">\n    视频加载中，请稍候... \n  </div> \n  <div class=\"a-p-info\"> \n   <label class=\"fl autoplayctrl\" suda-uatrack=\"key=videoq&amp;value=autoplay\" style=\"display:none;\"> <input type=\"checkbox\" checked=\"\" id=\"J_Video_Autoplay\" /> 自动播放 </label> \n   <span id=\"J_Video_Source\"> </span> \n  </div> \n </div> \n <div class=\"a-p-bd a-p-bd-b\" id=\"J_Play_List_Wrap\" style=\"display:none;\"> \n  <div class=\"a-p-slide\"> \n   <div class=\"a-p-s-list clearfix\" id=\"J_Play_List\"> \n    <div class=\"a-p-s-item J_Play_Item\" play-data=\"250920159-1-\" url-data=\"http://video.sina.com.cn/view/250920159.html\" title-data=\"中纪委落马官员被捕画面曝光\" source-data=\"\"> \n     <a href=\"javascript:;\" class=\"a-p-s-img\" hidefocus=\"true\" title=\"中纪委落马官员被捕画面曝光\"> <img width=\"120\" height=\"90\" src=\"http://p.ivideo.sina.com.cn/video/250/920/159/250920159_400_300.jpg\" alt=\"中纪委落马官员被捕画面曝光\" /> <i class=\"a-p-s-play\">play</i> <span class=\"a-p-s-txt\">中纪委落马官员被捕画面曝光</span> </a> \n    </div> \n   </div> \n   <a href=\"javascript:;\" class=\"a-p-s-prev\" id=\"J_Player_Prev\">向前</a> \n   <a href=\"javascript:;\" class=\"a-p-s-next\" id=\"J_Player_Next\">向后</a> \n  </div> \n </div> \n <script type=\"text/javascript\" charset=\"GBK\" src=\"http://www.sinaimg.cn/ty/sinaui/scrollpic/scrollpic2012070701.min.js\"></script> \n <script type=\"text/javascript\" src=\"http://www.sinaimg.cn/dy/js/jquery/jquery-1.7.2.min.js\"></script> \n <script type=\"text/javascript\" charset=\"GBK\" src=\"http://news.sina.com.cn/js/792/2015-03-13/149/videolist-2.js\"></script> \n <script type=\"text/javascript\">\r\n        /*自动播放1*/\r\n		//var AUTOPLAY = 1;\r\n		var AUTOPLAY = 0; //2015.11.25日通知改成0\r\n        /*连播1*/\r\